var suggestions=document.getElementById('suggestions');var userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(e){if(e.keyCode===191){e.preventDefault();userinput.focus();}
if(e.keyCode===27){userinput.blur();suggestions.classList.add('d-none');}}
document.addEventListener('click',function(event){if(!suggestions.contains(event.target)){suggestions.classList.add('d-none');}});document.addEventListener('keydown',suggestionFocus);function suggestionFocus(e){const focusableSuggestions=suggestions.querySelectorAll('a');const focusable=[...focusableSuggestions];const index=focusable.indexOf(document.activeElement);let nextIndex=0;if(e.keyCode===38){e.preventDefault();nextIndex=index>0?index-1:0;focusableSuggestions[nextIndex].focus();}
else if(e.keyCode===40){e.preventDefault();nextIndex=index+1<focusable.length?index+1:index;focusableSuggestions[nextIndex].focus();}}
(function(){var index=new FlexSearch({preset:'score',cache:true,doc:{id:'id',field:['title','description','content',],store:['href','title','description',],},});var docs=[{id:0,href:"/blog/2021/02/reading-service-principal-password-from-azure-devops/",title:"Reading service principal password from Azure DevOps",description:null,content:"\u003ch1 id=\"security\"\u003eSecurity\u003c/h1\u003e\n\u003cp\u003eGiven \u003ca href=\"https://docs.microsoft.com/en-us/azure/active-directory/develop/app-objects-and-service-principals#service-principal-object\"\u003eservice principal\u003c/a\u003e is eligible to manage your services and deployments.\nAlmost all the CI/CD services are coming with masking feature by default for these kinds of secret  values.\u003c/p\u003e\n\u003cp\u003eBy design of these CI agents, they are \u003ccode\u003estateless\u003c/code\u003e and each build they need sort of environment variable to work as your service principal.\nThis comes with a lot of flexibility where you can extend your setup without caring about previous state.\nDownside of this flexibility, every CI pipeline needs access to credentials where anyone can access them as well.\u003c/p\u003e\n\u003cp\u003eYour credential can be super safe inside the Azure world, in fact, they are not as long as someone has access to your CI pipeline.\u003c/p\u003e\n\u003ch1 id=\"reading-credentials\"\u003eReading credentials\u003c/h1\u003e\n\u003cp\u003eFor the sake of demonstration, I\u0026rsquo;ve published an example repo on \u003ca href=\"https://github.com/salimkayabasi/reading-service-principal-azure-devops\"\u003eGitHub\u003c/a\u003e.\u003c/p\u003e\n\u003ch2 id=\"how-to\"\u003eHow to\u003c/h2\u003e\n\u003cp\u003eOn your \u003ccode\u003eAzure DevOps\u003c/code\u003e pipeline, you need to grant the given pipeline to access \u003ccode\u003eservice principal\u003c/code\u003e credentials by just enabling a single flag.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan style=\"color:#0087ff\"\u003eaddSpnToEnvironment\u003c/span\u003e: \u003cspan style=\"color:#d75f00\"\u003etrue\u003c/span\u003e\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eThis flag will add service principal credentials as environment variables on your CI pipeline.\nAs shown \u003ca href=\"https://github.com/salimkayabasi/reading-service-principal-azure-devops/blob/main/azure-pipeline.yml#L20\"\u003ehere\u003c/a\u003e.\u003c/p\u003e\n\u003cp\u003eNext thing to do use reading values and \u003ca href=\"https://github.com/salimkayabasi/reading-service-principal-azure-devops/blob/main/azure-pipeline.yml#L21-L26\"\u003eprinting them\u003c/a\u003e in \u003ccode\u003ehex\u003c/code\u003e or \u003ccode\u003ebase64\u003c/code\u003e format. This will help you to cheat \u003ccode\u003emasking\u003c/code\u003e behaviour.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan style=\"color:#0087ff\"\u003einlineScript\u003c/span\u003e: |\u003cspan style=\"color:#00afaf\"\u003e\n\u003c/span\u003e\u003cspan style=\"color:#00afaf\"\u003e  echo \u0026#34;servicePrincipalId\u0026#34;\n\u003c/span\u003e\u003cspan style=\"color:#00afaf\"\u003e  xxd -p \u0026lt;\u0026lt;\u0026lt; $servicePrincipalId -c 256\n\u003c/span\u003e\u003cspan style=\"color:#00afaf\"\u003e  echo \u0026#34;servicePrincipalKey\u0026#34;\n\u003c/span\u003e\u003cspan style=\"color:#00afaf\"\u003e  xxd -p \u0026lt;\u0026lt;\u0026lt; $servicePrincipalKey -c 256\n\u003c/span\u003e\u003cspan style=\"color:#00afaf\"\u003e  echo \u0026#34;tenantId\u0026#34;\n\u003c/span\u003e\u003cspan style=\"color:#00afaf\"\u003e  xxd -p \u0026lt;\u0026lt;\u0026lt; $tenantId -c 256\u003c/span\u003e  \n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eJust a reminder, Azure DevOps pipelines will be using \u003ccode\u003eubuntu-latest\u003c/code\u003e VM unless you change it.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./assets/azure-devops-logs.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eActual values are masked for this image\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003ch2 id=\"reading\"\u003eReading\u003c/h2\u003e\n\u003cp\u003eAfter retrieving values as \u003ccode\u003ehex\u003c/code\u003e strings from your Azure Devops, you need to convert them to actual values back.\nIt can be simply done with few lines of \u003ccode\u003eNode.js\u003c/code\u003e implementation.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-js\" data-lang=\"js\"\u003e\u003cspan style=\"color:#0087ff\"\u003econst\u003c/span\u003e convert = (from, to) =\u0026gt; str =\u0026gt; console.log(Buffer.from(str, from).toString(to));\n\u003cspan style=\"color:#0087ff\"\u003econst\u003c/span\u003e hexToUtf8 = convert(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;hex\u0026#39;\u003c/span\u003e, \u003cspan style=\"color:#00afaf\"\u003e\u0026#39;utf8\u0026#39;\u003c/span\u003e);\n\nconsole.log(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;servicePrincipalId\u0026#39;\u003c/span\u003e);\nhexToUtf8(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;your servicePrincipalId goes here\u0026#39;\u003c/span\u003e);\nconsole.log(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;servicePrincipalKey\u0026#39;\u003c/span\u003e);\nhexToUtf8(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;your servicePrincipalKey goes here\u0026#39;\u003c/span\u003e);\nconsole.log(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;tenantId\u0026#39;\u003c/span\u003e);\nhexToUtf8(\u003cspan style=\"color:#00afaf\"\u003e\u0026#39;and tenantId here as well\u0026#39;\u003c/span\u003e);\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch1 id=\"using-them-on-github-actions\"\u003eUsing them on Github Actions\u003c/h1\u003e\n\u003ch2 id=\"adding-as-secret\"\u003eAdding as Secret\u003c/h2\u003e\n\u003cp\u003eLet\u0026rsquo;s create another secret for \u003ccode\u003eGithub Actions\u003c/code\u003e. To do that, we need one more thing called \u003ccode\u003esubscriptionId\u003c/code\u003e.\nThe \u003ccode\u003esubscriptionId\u003c/code\u003e can be found on Azure Portal or simple \u003ccode\u003epowershell\u003c/code\u003e or \u003ccode\u003ebash\u003c/code\u003e command like\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-powershell\" data-lang=\"powershell\"\u003e\u003cspan style=\"color:#0087ff\"\u003eGet-AzSubscription\u003c/span\u003e\n\nName                 Id             TenantId State\n----                 --             -------- -----\nName Of Subscription subscriptionId tenantId Enabled\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eAdd new \u003ccode\u003eSecret\u003c/code\u003e as JSON format on your project or organisation on GitHub.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-json\" data-lang=\"json\"\u003e{\n  \u003cspan style=\"color:#0087ff\"\u003e\u0026#34;clientId\u0026#34;\u003c/span\u003e: \u003cspan style=\"color:#00afaf\"\u003e\u0026#34;value of servicePrincipalId\u0026#34;\u003c/span\u003e,\n  \u003cspan style=\"color:#0087ff\"\u003e\u0026#34;clientSecret\u0026#34;\u003c/span\u003e: \u003cspan style=\"color:#00afaf\"\u003e\u0026#34;value of servicePrincipalKey\u0026#34;\u003c/span\u003e,\n  \u003cspan style=\"color:#0087ff\"\u003e\u0026#34;subscriptionId\u0026#34;\u003c/span\u003e: \u003cspan style=\"color:#00afaf\"\u003e\u0026#34;your subscriptionId\u0026#34;\u003c/span\u003e,\n  \u003cspan style=\"color:#0087ff\"\u003e\u0026#34;tenantId\u0026#34;\u003c/span\u003e: \u003cspan style=\"color:#00afaf\"\u003e\u0026#34;value of tenantId\u0026#34;\u003c/span\u003e\n}\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./assets/github-secrets.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n  \n\u003c/figure\u003e\n\n\u003ch2 id=\"accessing-from-github-actions\"\u003eAccessing from Github Actions\u003c/h2\u003e\n\u003cp\u003eOn your github actions workflow file, you can try to log in with using given credentials. Full example can be found on \u003ca href=\"https://github.com/salimkayabasi/reading-service-principal-azure-devops/blob/main/.github/workflows/azure.yml#L7-L12\"\u003eGitHub repo\u003c/a\u003e as well.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan style=\"color:#0087ff\"\u003esteps\u003c/span\u003e:\n  - \u003cspan style=\"color:#0087ff\"\u003euses\u003c/span\u003e: actions/checkout@v2\n  - \u003cspan style=\"color:#0087ff\"\u003euses\u003c/span\u003e: azure/login@v1\n    \u003cspan style=\"color:#0087ff\"\u003ewith\u003c/span\u003e:\n      \u003cspan style=\"color:#0087ff\"\u003ecreds\u003c/span\u003e: ${{ secrets.AZURE_CREDENTIALS }}\n  - \u003cspan style=\"color:#0087ff\"\u003erun\u003c/span\u003e: az group list\n  - \u003cspan style=\"color:#0087ff\"\u003ename\u003c/span\u003e: Azure logout\n    \u003cspan style=\"color:#0087ff\"\u003erun\u003c/span\u003e: az logout\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./assets/github-actions-logs.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eSuccessfully logged in\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003ch1 id=\"conclusion\"\u003eConclusion\u003c/h1\u003e\n\u003cp\u003eIt is possible to retrieve credentials and use them in a different CI*. Level of trust is important in the company level.\nIn order to prevent these kinds of issues, you can rotate credentials, but then they can be retrieved again.\u003c/p\u003e\n\u003cp\u003eAs I said, if any person who is granted to access your CI then s/he can do whatever they like.\nThe purpose of this article, I just wanted to highlight that these ways of protections are useless and technically possible to read secrets.\nSame goes for \u003ccode\u003eGithub Actions\u003c/code\u003e as well and there is no way to keep those secrets away from people who work for company.\u003c/p\u003e\n\u003cp\u003eIf you didn\u0026rsquo;t like Azure DevOps and still want to use \u003ccode\u003eGithub Actions\u003c/code\u003e, it is not that hard to migrate your CI to wherever you want.\u003c/p\u003e\n\u003cp\u003e\u003cem\u003e*By the way, Github Actions and Azure DevOps are using same virtual machines under the hood.\u003c/em\u003e\u003c/p\u003e\n\u003ch5 id=\"credits\"\u003eCredits\u003c/h5\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href=\"https://pascalnaber.wordpress.com/2020/01/04/backdoor-in-azure-devops-to-get-the-password-of-a-service-principal/\"\u003ePascal Naber\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:1,href:"/blog/",title:"Blog",description:"My blog posts",content:""},{id:2,href:"/blog/2018/01/ne-zaman-i%C5%9F-de%C4%9Fi%C5%9Ftirmemiz-gerekiyor/",title:"Ne zaman iş değiştirmemiz gerekiyor?",description:null,content:"\u003cp\u003eZamanı geldi ve artık iş değiştirmek istediğinize karar verdiniz. Peki gerçekten de iş değiştirmek için beklediğiniz olgunlaşma evresi tamamlandı mı? En önemlisi bunu yapmaya hazır mısınız?\u003c/p\u003e\n\u003cp\u003eBu haftaki yazımda iş değiştirmeye giden yolda karar vermeye etki eden başlıkların altını çizmeye çalışacağım.\u003c/p\u003e\n\u003cp\u003eSektörden sektöre alışkanlıklar ve davranışlar değişmektedir. Hatta bazen şirketten şirkete bile değişkenlik gösterecektir. İçinde bulunduğumuz iletişim çağında size bu kararı aldıran birden çok uyaran vardır. Bunları bir kaç başlık altında toparlayarak başlayalım.\u003c/p\u003e\n\u003ch3 id=\"ofiste-huzurum-kalmadı\"\u003eOfiste huzurum kalmadı\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eHer sabah yataktan kalktığınızda bu saatte işe mi gidilir?\u003c/li\u003e\n\u003cli\u003eAkşam olsa da eve gitsek?\u003c/li\u003e\n\u003cli\u003eŞimdi bu toplantıya kim girecek?\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003egibi örneklerle başlayan ve giderek artan şikayetlerin temelinde ofisteki huzursuzluklar yatıyor olabilir.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/huzursuz.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n  \n\u003c/figure\u003e\n\n\u003cp\u003eBilinen adıyla mobbing ya da gizli adıyla \u003cem\u003e\u0026ldquo;office politics\u0026rdquo;\u003c/em\u003e, çalışma arkadaşlarınızın size karşı farkında ol(may)arak uyguladığı bir baskıdır. Toplumun bir çok konusunda kendisini ağır şekilde gösteren mahalle baskısının bir çeşit ofis şartlarına göre uyarlanması diyebiliriz.\u003c/p\u003e\n\u003cp\u003eÇalıştığınız firmada toplam çalışma süresi olarak ortalama bir süre varsa ve siz de bu sürenin sonuna yaklaştıkça bu ve buna benzer birkaç baskı hissedebilirsiniz. Zamanında Hürriyet gazetesinde çalışırken, binanın girişinde uzun zamandır çalışanların adının listelendiği bir köşe vardı. 50 yıl, 40 yıl üstü çalışanların olduğu bölümü gördükçe ağlamaklı oluyordum.\u003c/p\u003e\n\u003cp\u003eBizim sektörde bu durum bu kadar da doğru karşılanmıyor. Bazı firmalarda 4–5 yıl, bazı firmalarda ise 8–9 ay olabiliyor bu süre. Şirket içinde mutlu ve huzurlu kalabildiğiniz süreye göre hesaplamak önemlidir. Bu döngünün ideal değeri diye bir durum yoktur ve etrafımda gördüğüm kadarıyla insanlar sadece yakın çevresine göre referans alıp bu döngüyü hesaplıyorlar. Zaman içerisinde bu durum sizin için kapalı bir ekosisteme mahkumiyete yol açacaktır. Yaşanmakta olan bilgi çağında, iletişimde ve paylaşımda olduğunuz insan/firma sayısını fazla tutmak faydanızadır.\u003c/p\u003e\n\u003cp\u003eOfis arkadaşlarınız ile iletişiminizin iyi olması, yönetici ile kurduğunuz samimiyet ve rekabet yerine takım oyunu varsa çalıştığınız şirkette geçen sürenizi uzun tutabilirsiniz. Aksi durumlarda ise canınızı sıkan bir durumla karşılaştığınızda lütfen tekrarı olmaması için önlemleri almayı unutmayın.\u003c/p\u003e\n\u003cp\u003eBir de sizin başarılarınızı kıskanıp her fırsatta mesleki olarak sizin bireysel gelişimizin önüne geçmeye çalışan insanlar olabilir. Hatta bazı durumlarda sizin gelişim için verdiğiniz çabalarınızı ona karşı bir kasıt içinde yaptığınızı iddia edenler bile olabilir. Bu tarz insanlara denk geldiyseniz, yine hızlıca iş değiştirme vakti gelmiştir.\u003c/p\u003e\n\u003ch3 id=\"mesleki-tatmin\"\u003eMesleki tatmin\u003c/h3\u003e\n\u003cp\u003eYeni mezun birisi ile aynı takımda daha tercübeli iki çalışan arasında da bu süreç farklı olacaktır. Özellikle sektördeki ilk yıllarınızda, görece yeni teknolojileri takip eden, kendinizi geliştirebileceğiniz insanlarla çalışmayı tercih etmeye özen göstermelisiniz. Eğer size gelip de biz hala Adobe Flash ile websitesi yapıyoruz diyen bir ekip varsa arkanıza bile bakmadan kaçabilirsiniz.\u003c/p\u003e\n\u003cp\u003eTakıma dahil oldunuz, hem birşeyler öğreniyor hem de öğretiyorsunuz. Demek ki sayılı şanslı insan arasına girmişsiniz demektir. Peki mesleki açıdan olası sorunlar ne olabilir? Görece tecrübeli ama iletişim yönünden zayıf takım arkadaşları ile etkileşim içinde bulunmak cidden zor olabilir.\u003c/p\u003e\n\u003cp\u003eTakım lideriniz de tüm ekip arkadaşlarına kişisel gelişim konusunda destekleyici olması, size ve ekibe genel olarak faydalı olacaktır.\u003c/p\u003e\n\u003cp\u003eBu konuda Google’da uygulanan \u003cem\u003e\u0026ldquo;20% time policy\u0026rdquo;\u003c/em\u003e olarak bilinen, çalışma vaktinizin beşte birini kişisel projeler/motivasyonunuz için ayırma özgürlüğünü araştırabilirsiniz.\u003c/p\u003e\n\u003ch3 id=\"kariyer-hedefleri\"\u003eKariyer hedefleri\u003c/h3\u003e\n\u003cp\u003eLütfen kendinize kısa ve uzun vadeli kariyer hedefleri koymayı unutmayın. Bu hedefler arasında hedeflediğiniz bir pozisyona ya da takıma geçmek de olabilir. Daha bireysel hedefler olarak saymak gerekirse; open-source bir projeye destek vermek, daha önce ilgilenmediğim bir teknolojiyi öğrenmek gibi başlıklar olabilir.\u003c/p\u003e\n\u003cp\u003eBu hedeflerinizi koyma konusunda ya da koyduktan sonra uygulama konusunda motivasyon sorunları yaşanıyor olabilir. Ekipteki arkadaşlarınızın bu çalışmalarınıza destek olmaması, şirket içindeki stres ya da yoğun mesai sonrası enerjinizin kalmaması gibi dış etkenlerle örneklenebilir.\u003c/p\u003e\n\u003cp\u003eAyrı bir konu da size verilen görevlerin ekipteki diğer arkadaşlarınıza göre daha düşük öncelikli olması ya da kendinizi göstermenize yardımcı olmaması da motivasyonunuzu düşüren bir sebep olabilir. Teknik olarak ekibin bir parçası olarak hissetmenizin önüne geçiyorsa, kariyer hedeflerinize giden yolda gecikmelere yol açacaktır.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/github.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eGithub üzerinden de kendinizi takip edebiliriniz\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003ch3 id=\"maaş-beklentileri-ve-piyasa-dengesi\"\u003eMaaş beklentileri ve piyasa dengesi\u003c/h3\u003e\n\u003cp\u003eHer zam dönemi, insan kaynaklarından beklenen güzel haber için heyecan yapılır. Öncesinde bir kaç görüşme ile performans değerlendirmesi yapılır. Bütçeler, hedefler konuşulur ve o büyük an gelir zarfı açarsınız.\u003c/p\u003e\n\u003cp\u003eYazılım dünyasında dünya piyasasını takip eden maaşları yakalamak gerektiğine inanıyorum. Bu sebeple ben geçmişten beri aldığım maaşı sürekli olarak dolar ve euro karşısındaki değerini de güncel kura göre bir yerlere yazıyorum. Bazen zam aldığınız halde bir önceki yıla göre alım gücünüzün düştüğünü hissederseniz, sebebini anlamış olursunuz.\u003c/p\u003e\n\u003cp\u003eTürk Lirası’nın diğer para birimleri karşısında %15 değer kaybettiği bir dönemde %10 zam almanız, alım gücünüzde bir iyileştirme yapmamış olur. Tabiki de bu rakamda pazarlık yaparken ülke ekonomisindeki diğer parametrelere de göz atmak gerekir.\u003c/p\u003e\n\u003cp\u003eBir alanda giderek artan tecrübenizle birlikte daha fazla maaş istemek tabiki de doğal hakkınızdır. Beklentiniz karşısında;\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eMaaş dengesi tutturmaya çalışıyoruz\u003c/li\u003e\n\u003cli\u003eAma zaten herkese %X zam yapıldı\u003c/li\u003e\n\u003cli\u003eBu sene küçülmeye gidiyoruz\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eve benzeri cevaplar alabilirsiniz. Siz de karşılık olarak, varsa diplomalarınızı, projelerdeki katkılarınızı, ekip yönetimi gibi sizi diğer arkadaşlarınızdan öne çıkaracak özelliklerinizi vurgulamayı unutmayın. Rakip firmalardaki benzer tecrübe ve pozisyondaki insanların nasıl bir maaş aralığında olduğunu öğrenmeye çalışmak da faydalı olacaktır.\u003c/p\u003e\n\u003cp\u003eUnutmayın, iş veren sizi satın almıyor. Siz onlara emeğinizi satıyorsunuz ve bir çok piyasada olduğu gibi fiyatı alıcı kadar üretici/satıcı da belirler. Pazarlık yapmaktan korkmayın. Benzine gelen her zaman sizin cebinize de etki ediyor.\u003c/p\u003e\n\u003cp\u003eBir de en azından burada maaşım gününde yatıyor diyerek güzelleme yapan arkadaşlarınız varsa, lütfen bunun olması gereken bir durum olduğunu bu tavır ile sektördeki bu sorunu gideremeyeceğimizi anlatmamız gerekiyor.\u003c/p\u003e\n\u003cp\u003eBazı firmalarda da verilen yan haklar tercih sebebi olabiliyor ama unutmayın ki bu verilen haklar sözleşmede hiç bir yerde yazmaz. Bu sebeple de bu hakların bir kısmı değişebilir ya da tamamen iptal edilebilir. Pazarlık yaparken bu hakları size hediye ediyoruz diye başlayıp fiyat düşürmeye çalışıldığında aslında zorunlu olarak o hizmetleri almış oluyorsunuz. Kredi çekeren yapılan \u003cstrong\u003ezorunlu\u003c/strong\u003e hayat sigortası gibi bir bankacılık taktiğini anımsatıyor bana.\u003c/p\u003e\n\u003ch3 id=\"ya-ben\"\u003eYa ben\u003c/h3\u003e\n\n\u003cdiv style=\"position: relative; padding-bottom: 56.25%; height: 0; overflow: hidden;\"\u003e\n  \u003ciframe src=\"https://www.youtube.com/embed/AUVRPnR33g4\" style=\"position: absolute; top: 0; left: 0; width: 100%; height: 100%; border:0;\" allowfullscreen title=\"YouTube Video\"\u003e\u003c/iframe\u003e\n\u003c/div\u003e\n\n\u003cp\u003eSektöre karşı bakış açımı yıllar önce bu videoda bulmuştum. Siz de kendiniz için en iyi işi bulana kadar arayıştan çekinmeyin.\u003c/p\u003e\n\u003cp\u003eŞu ana kadar bir çok defa iş değiştirdim. Bunların bazılarında benimle teknik konularda kişisel konulardan dolayı aşırı çatışmaya girilmesi, maaşlar ve ödemeler konusunda sorunlar olması, yönetimdeki insanların bizleri köle olarak görmesi gibi sorunlarla genelde iş değiştirmeyi tercih ettim.\u003c/p\u003e\n\u003cp\u003eSektörde \u003cstrong\u003eoutsource\u003c/strong\u003e olarak kabul edilen ama size \u003cem\u003e\u0026ldquo;biz sizi danışman olarak gönderiyoruz ve resmen sizden kar etmiyoruz\u0026rdquo;\u003c/em\u003e diyen firmalar da hatırı sayılır derecede bulunuyor. Buralardan da geçmiş birisi olarak, belirli bir dönemde hem teknik hem de insan yönetimi konusunda sizlere çok şey katabilir ama uzun vadede uzak durulması gerekiyor.\u003c/p\u003e\n\u003cp\u003eAslında bu yazıyı yazmamdaki sebeplerden birisi de an itibariyle çalıştığım firmadaki \u003cstrong\u003eson\u003c/strong\u003e günüm olduğu için. 11 aylık bir AUTO1 macerasından sonra yarın yeni bir yolculuğa başlıyorum. Türkiye’de olduğu gibi Almanya’da da eylemlerimde devam ediyorum. Bekleyelim ve görelim, bir sonraki macera nasıl olacak.\u003c/p\u003e\n\u003cp\u003eİş değiştirmekten korkmayın ve unutmayın ki bir şirkete, ekibe, takıma, teknolojiye bağlı kalmak uzun vadede sizi bağımlı hale getirecektir. Teknolojiyi takip etme konusunda yavaş kalmanız aslında sektörde de giderek sizi geriye atacaktır. İş değiştirmek bir sanattır ama gelen de gideni aratır diye bir laf var kültürümüzde, dikkatli olunması gereken bir konudur.\u003c/p\u003e\n"},{id:3,href:"/blog/2018/01/almanya-var-da-bir-tek-bana-m%C4%B1-var/",title:"Almanya var da bir tek bana mı var?",description:null,content:"\u003cp\u003eBu hafta bahsetmek istediğim konu geçen bir senede Türkiye’den Almanya’ya taşınma kararı almamla birlikte yaşadığım süreçleri toparlamak istedim.\u003c/p\u003e\n\u003cp\u003eGeçtiğimiz günlerde bir anket yapmıştım ve en çok merak uyandıran ikinci konu olarak çıkmıştı. Yurt dışında yaşamak isteyen insanlar için fikir vermesi açısından genel olarak ilk adaptasyon ve sonrasındaki konuları geniş geniş değerlendireceğim.\u003c/p\u003e\n\u003cp\u003eTakvimler 20 Şubat 2017, uzun zamandır beklediğim o tek yön bileti aldım, uçağa bindim ve ver elini Berlin.\u003c/p\u003e\n\u003ch3 id=\"yaşam-şartları\"\u003eYaşam şartları\u003c/h3\u003e\n\u003cp\u003eHava net bir şekilde soğuk. Benim gibi İstanbul’dan geliyorsanız ilk başta alışması biraz vakit alıyor. Ama ciddi oranda Ankara ya da Eskişehir havasına yakın diyebilirim. Şimdiye kadar 2–3 kere yerde tutacak kadar kar yağdı. Bunun dışında yoğun yağmurlu geçiyor günler. Hava sıcaklığı en düşük -5 civarında denk geldim, yazın da 33–35 derece civarında oluyor.\u003c/p\u003e\n\u003cp\u003eDeniz yok :) Eğer ben yaşayamam diyorsanız, bunu tekrar düşünün ama yeterince nehir ve göl var. Hem de kolayca ulaşılabilecek şekilde.\u003c/p\u003e\n\u003cp\u003eÇoğunlukla insanlar birbirine karşı kibar davranıyorlar, bazı konularda dil yüzünden anlaşamıyoruz ve trene bakar gibi bakıyoruz. Böyle zamanlar çok sık denk gelmediği için idare edilebilir oluyor.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/renting-costs.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003ekiralar çok yüksek\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eKira masrafı Berlin’de giderek artan bir masraf kalemi ve ciddi oranda tepkilere yol açıyor. Yasalara göre ev sahibiniz, siz oturmak istedikçe ciddi bir gerekçe gösteremezse sizi evden çıkaramaz ve abartı bir zam da yapamaz. Bu sebeple piyasanın yarı fiyatına oturduğu için evini değiştirmekten çekinen çok fazla insan var. Uzun yıllar aynı evde oturmak ciddi tasarruf sağlıyor. Ülkemizdeki \u0026ldquo;inşaat ya resulallah” mottosunun tersine çok fazla da yeni yapılan bina bulmak zor. Bazı mahalleler de evler 1910 yapımı ama içi yeni tadilat görmüş olabiliyor.\u003c/p\u003e\n\u003cp\u003eGeldikten sonra etrafımda en çok aldığım uyarı illegal içerikler hakkında oldu. Ofiste yeterince rus arkadaş olmasının da etkisi olabilir. Sakın ama sakın gidip de torrent kullanayım ya da bir dizi izleme sitesi kullanayım diye aklınızdan geçirmeyin. Bazı durumlarda VPN olsa dahi başınız belaya giriyor. Maddi olarak ciddi yaptırımları bulunuyor. Buna çözüm olarak biz \u003ca href=\"https://put.io/\"\u003eput.io\u003c/a\u003e kullanıyoruz. Şimdilik bir sorun yaşamadık ama şimdilik.\u003c/p\u003e\n\u003cp\u003eNetflix ya da diğer içerik sağlayıcı internet sitelerinde bazı filmlerin sadece Almanca alt yazı ile verilmesi dışında ciddi bir sıkıntı yaşamadım.\u003c/p\u003e\n\u003cp\u003eIrkçılık yaşayıp yaşamadığımızı soranlar çok oluyor. Genel olarak şunu söyleyebilirim ki ülkemizdeki mülteci sorunu sonrasında bizlerin Suriye’den gelen insanlara yaklaşımımız karşısında burada bir ırkçılık var demek imkansız. Hatta bırakalım Suriye sorununu, sırf Kürt olduğu için sıkıntı yaşayan bir çok insan varken burada buna benzer bir durum yok denebilir. Sanırım nazi dönemi sonrasında Almanya bu konuda çok ciddi yol almış görünüyor. Tabiki de dil ve kültür yakınlığı gibi sebeplerden pozitif ayrımcılık yaşandığı oluyor.\u003c/p\u003e\n\u003cp\u003eŞubat ayından belki de Ağustos ayına kadar ortalama her iki haftada bir Türkiye’ye uçmam gerekiyordu. Berlin’de ve İstanbul’da ikişer tane havaalanı var. Her iki yönde de her gün ciddi sayıda her firmadan uçuş bulunuyor. Ek olarak Antalya, Trabzon, Adana ve Ankara’ya da direk uçuşlar var. Bir ayağınız sürekli olarak Türkiye’de gibi hissediyorsunuz. THY özellikle 30kg bagaj sınırını konusunda çok esnek ve yardımcı oluyor. 44kg ile sorunsuz ve ek masrafsız yolculuk yapanlar var aramızda :) Fiyatlar konusunda da genel olarak buradan gidiş dönüş alınan biletler, tersi yöne göre %20 civarında daha uygun oluyor. Bir ikisinde de kampanyaya gelirseniz, gayet uygun fiyata seyahat seçeneği vardır.\u003c/p\u003e\n\u003ch3 id=\"ulaşım\"\u003eUlaşım\u003c/h3\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/transportation.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eby Dreamstime.com\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eToplu taşıma ağı, özellikle metro sistemi gayet ideal bence. O bahsedilen alman disiplini, hiç bir zaman gecikmeyen trenler ile dolu yalanları bir kenara bırakırsak genel olarak gideceğiniz yere 1–2 dakika sapma dışında geç kalmıyorsunuz.\u003c/p\u003e\n\u003cp\u003eSokakta sık sık Car2go ya da DriveNow yazan arabalar göreceksiniz. Bunlar bir hesap açtırdıktan sonra uygulama üzerinden dakika bazında kiralama yapabildiğiniz arabalardır. Sadece araba değil dilerseniz bisiklet, motorsiklet falan da kiralama yapabilmeniz için çok fazla firma bulunuyor.\u003c/p\u003e\n\u003cp\u003eBu araçları ve uygulamaları biraz deneyince, bu ülkede startup yapmanın ne kadar kolay olacağını düşündüm. Çünkü Türkiye’de çalıştığım tüm startup’ların en büyük sorunu fraud yakalama ve engelleme üzerine oluyordu. Buna benzer hizmeti İstanbul’da yapmaya çalışırsanız sanırım ertesi gün arabayı Adana’da bir oto sanayide bulursunuz.\u003c/p\u003e\n\u003ch3 id=\"çarşı-pazar\"\u003eÇarşı, pazar\u003c/h3\u003e\n\u003cp\u003eMarket alışverişi ve diğer harcama kalemleri için uygun fiyatlı bir çok alternatif bulunuyor. Eğer evinize yakın bir Edeka şubesi varsa kiranızda artışa bile sebep olabilir. Her metro istasyonuna yakın bir Edeka olması da aynı sonucu doğuruyor olabilir tabiki. Edeka genel olarak ürün çeşitliliği ve kalite olarak Migros seviyesinde diyebiliriz. Daha uygun fiyatlı ürünler için Lidl, Aldi ve Netto diye alternatifler de bulunuyor. Genel olarak ortak sattıkları marka sayısı çok az denebilir. Özellikle bir ürünü arıyorsanız, direk onu satan markete gitmek gerekebiliyor.\u003c/p\u003e\n\u003cp\u003ePazar günü her yer kapalı. İlk başta çok zorlanmıştım ama alışınca gayet huzurlu geliyor. Bildiğim kadarıyla pazar günü işletmenizi açık tutmanız için çalışanların tamamının aile üyeleri ve kilise vergisinden muaf olmanız gerekiyor. Tabiki bu kısıtlama sadece satış yapılan yerler için geçerli, pazar günü aç kalmak diye bir durum yok tabiki.\u003c/p\u003e\n\u003cp\u003eAyda bir pazar günü de, Shopping Sunday -pazar alışverişi- olarak tanımlanıyor ve dükkanların yarım gün çalışmasına izin veriliyor. Bir kaç tane merkezi yerdeki özel izin almış süper market dışında gerçekten pazarları açık market bulmak imkansız.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/istanbul-market.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eİstanbul Market @kotti\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eAlıştığınız markaları ve kaliteleri de çok rahat bulabiliyorsunuz. Gıda ürünlerinde türk marketlerinde alışveriş yaparken hangi ülkede olduğunuzu şaşırabilirsiniz. Biber de yok. Evet, evet bildiğimiz biber hiç bir Alman marketinde yok. Özellikle Türk marketine gitmeniz gerekiyor.\u003c/p\u003e\n\u003cp\u003eBunun dışında tekstil sektöründe de sık sık yerli markaları raflarda görebiliyorum. Genelleme yapmak gerekirse; ürünler Amerika/Avrupa markaları ise Türkiye’den ucuz olacaktır ama Türk markaları genelde burada biraz daha pahalı. Basitçe vergi ve lojistik masrafları düşünebilirsiniz.\u003c/p\u003e\n\u003ch3 id=\"evrak-işleri\"\u003eEvrak işleri\u003c/h3\u003e\n\u003cp\u003eAlmanya sanırım baştan aşağıya evrak üzerine kurulu bir sistem. Size gelen hiç bir postayı çöpe atmayın ve herhangi bir yerde bu evrağın orijinalini göstermeniz gerekebileceğinizi unutmayın.\u003c/p\u003e\n\u003cp\u003eİlk geldiğiniz 2 hafta içerisinde, bizdeki muhtarlıktan adres kaydına benzer bir işlem yapılması gerekiyor. Bunu yapmadan hiç bir işleminiz yapılmıyor. Banka hesabı açmak, sağlık sigortası yaptırmak gibi temel işlemlerde bile bu evrak soruluyor. Posta bile alamazsınız, oturduğunuz yeri ispat edecek evrağınız yok diyeceklerdir.\u003c/p\u003e\n\u003cp\u003eSürekli olarak bu işlemleri takip etmeniz gerekiyor. Adres kaydı ile birlikte vergi numaranız geliyor. Bunu alıp sağlık sigortanızı başlatıyorsunuz. Sonrasında bu iki evrağı alıp yabancılar bürosununa gidip teslim ediyorsunuz. Uzun uzun devam eden ve birini tamamlamadan diğerine geçemediğiniz bir evrak silsilesi vardır. Adeta parası neyse vereyim şu bölümü geçeyim diyeceğiniz mobil oyun gibi.\u003c/p\u003e\n\u003cp\u003eKalıcı olarak geldiğinizden sonra ehliyetiniz 6 ay boyunca geçerli ve sonrasında belirli bir sınava girerek buradaki sistemlere uyumlu yeni bir ehliyet ile elinizdeki ehliyetinizi değiştirmeniz gerekiyor. Toplu taşıma yeterli olduğu için buna ihtiyaç duymadım şimdilik.\u003c/p\u003e\n\u003ch3 id=\"almanca-hakkında\"\u003eAlmanca hakkında\u003c/h3\u003e\n\u003cp\u003eGünlük hayatınızda alışveriş, yeme içme gibi aktivitelerde sürekli olarak \u0026ldquo;bitte schön”, \u0026ldquo;danke schön” laflarını duyuyor olacaksınız. İlk geldiğimde ofis arkadaşlarım Almanca’da en çok kullanılan cümleler hakkında bir istatistik paylaşılmıştı. Ama biz birinci sıraya \u0026ldquo;saucen alles?” ve \u0026ldquo;salad alles?” sorularını ekledik. O kadar çok dönerci var ki, bu sorular gün içerisinde kaç defa kullanılıyor hiç emin değilim. Dönerin içerisine değişik soslar ve salata eklenerek servis ediliyor.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/mustafas-gemuse.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eMustafa’s Gemüse Kebap — Mustafa’nın sebzeli kebabı\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eGenel olarak Berlin’de yerel insanlar İngilizce konusunda yeterli seviyedeler denebilir. Çok fazla göçmen ve turist almasının da buna etkisi büyüktür. Evinize kargo getiren adam ya da bazı devlet daireleri dışında bugüne kadar bir sorun ile karşılaşmadım. Sizin de bir kaç kelime almanca konuştuğunuzu görünce ciddi şekilde sevinip yardımcı olmaya çalışıyorlar.\u003c/p\u003e\n\u003cp\u003eSokakta konuşulan ikinci dil sanırım Türkçe olmalı. Çok sıklıkla birilerini Türkçe konuşurken duyabilirsiniz. Hem Almanya’nın hem de Avrupa’nın en yoğun Türk göçmen popülasyonu Berlin’de yaşıyor diye biliyorum.\u003c/p\u003e\n\u003cp\u003eTamamen İngilizce kullanarak hiç bir şekilde Almanca öğrenmeden de hayatınızı idame edebilirsiniz Berlin’de ama diğer şehirlerde bunun bu şekilde olmayacağını söyleyen çok fazla Alman iş arkadaşım oldu.\u003c/p\u003e\n\u003cp\u003eTürkiye’de sokakta dil kursu ilanı dağıtan insanlara sık sık denk gelirsiniz. Burada işler biraz değişik. Çok fazla Almanca için dil kursu reklamı var etrafınızda ve hatta bazıları Almanca dilinde ilan vermekten de çekinmiyorlar. Almanca öğrenmek ister misiniz diye soruyu Almanca olarak sormak bana çok garip gelmişti. Berlin’de biraz dil öğrenmek işlerinizi kolaylaştırır, çok rahat edersiniz, gelin öğrenin diye mahalle baskısı var gibi görünüyor. Ben sadece sayıları biliyorum ve daha ilerisine de çok ihtiyacım olmadı şu zamana kadar. En zor anınızda size tercüme yapacak bir Türk bulmak çok kolay. En garibi de Türk olduğunu anlamadan İngilizce konuşmaya başladığınız kişinin durumu fark edince rahatlayıp, \u0026ldquo;Beni niye yoruyorsun iki saattir” diye hafif tebessümle kızması.\u003c/p\u003e\n\u003ch3 id=\"çalışma-hayatı\"\u003eÇalışma hayatı\u003c/h3\u003e\n\u003cp\u003eTabiki de çalışılan firmalar farklılıklar gösterse de Berlin genel olarak startup ve/veya çok uluslu firmalarla dolu. Bu sebepten dolayı çok farklı seviyeden ve kültürden insanla birlikte çalışıyorsunuz.\u003c/p\u003e\n\u003cp\u003eOfis hayatındaki gündelik ya da iş ile konularda farklı kültürlerdeki insanların farklı noktalara vereceği tepkileri öğrenmeniz vakit alabiliyor. Genel olarak herkes karşısındakine geri bildirim verme konusunda şeffaf olmaya ve elinden geldiğince kibar davranmaya özen gösteriyor.\u003c/p\u003e\n\u003cp\u003eBazı istina durumlar dışında beklenen işi öngörülen sürede bitirmek yeterlidir. Fazla mesai ya da izin alırken sıkıntı yaşanması gibi bir durumu pek duymadım. Yasalara göre 20 iş günü yasal izniniz var ama genelde bu rakam bizim sektörde 24 gün olarak kabul ediliyor. Bazı firmalarda ise 30\u0026rsquo;a kadar çıkıyor bu rakam. Bir kaç dini bayramı da eklerseniz, yıllık 6–7 haftalık tatiliniz var. Bu sebeple izinleri bitirmek gibi bir derdimiz var. Gerçekten bitiremediğiniz zaman bu izinleri kaybettiğiniz için Mart ayında ofis genel olarak sakin oluyor. Her sene Mart ayına bu izinler sıfırlanıyor.\u003c/p\u003e\n\u003cp\u003eAlmanya ekonomisi, görece stabil olduğundan masraflarınızda bir artış olasılığı düşüktür. Aynı sebepten gelirinizde de bir zam beklemeyin :) Unutmayın, Türkiye’de zam aldığınızda aslında enflasyon ve döviz karşısında değer kaybeden maaşınıza düzeltme yapılıyor. Sektöre ilk girdiğiniz maaşı o günkü kur üzerinden dolar ya da euro olarak hesaplayın ve şimdiki maaşınızı hesaplayın, anlayacaksınız.\u003c/p\u003e\n\u003ch3 id=\"türk-mahallesi\"\u003eTürk mahallesi\u003c/h3\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/love-kotti.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eI love Kotti\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eAlmanya genel olarak düzenli bir görüntü verse de yukarıda gördüğünüz İstanbul Market’in olduğu mahalle %90 oranında türk nüfusunun olduğu bir mahalle. Buraya geldiğinizde hala aynı ülkede olup olmadığınızı düşünebilirsiniz. Kottbusser Tor (a.k.a Kotti) olarak bilinen bu durakta indiğinizde tüm tabelaların ve sokağın dili birden değişecektir. Her tarafta popüler türk restaurantları sizi karşılıyor. En meşhurlarından birisi \u0026ldquo;Hasır” ve burada yaşayan diğer insanlar tarafından da çok beğeniliyor. Türk mutfağını kaliteli etlerle denemek isteyenler için cidden güzel bir mekan.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/hasir-restaurant.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eHasır Restaurant\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eMetrekare başına düşen çanak anten sayısı, gözle görülür seviyede düzensiz ve pis sokaklar, bağırış çağırış içindeki esnaf ile aradığınız memleket ortamı bir metro uzaklığında.\u003c/p\u003e\n\u003cp\u003eBana en zor gelen durum yemekten sonra çay istediğimizde bunun fiyatının olduğunun hatırlatılmasıdır. Bir çok mekanda çay fiyatı menüde yazıyor. Yemekten sonra çay ikramını yapan bir çok yer çay fiyatını yediğiniz diğer ürünlere göre fiyata ekleyip eklememeye karar veriyor. Bazen siparişi İngilizce verdiğim sonradan Türk olduğumu anlayan mekanlarda gidip çayı adisyondan çıkaranlar da olmadı değil. Yer yer değişkenlik gösteriyor.\u003c/p\u003e\n\u003cp\u003eHayatın her anında Türkçe konuşan birilerinin olması bazen işinizi çok kolaylaştırıyor. Yardımsever bir millet olduğumuz için işlerimizi hızlıca halledip çıkabiliyoruz.\u003c/p\u003e\n\u003cp\u003eBurada doğup büyüyen bir çok Türkiye kökenli insanın Türkiye hakkındaki yorumları bazen sizi farklı noktalara götürüyor. Bir kısmı buradaki hayata entegre olmamak için direnen insanlar var, her gün geri dönme hayali içindeler ve ne yazıkki siyasi iktidarın göz boyamasına ciddi oranda inanmış durumdalar. Eski Almanya kalmadı laflarını çok sık duyuyorum. Eskiden çok daha iyiydi artık şunları yapamıyoruz diye şikayet edilen seviyeye Türkiye’de ulaşmak için daha 40 fırın ekmek yememiz gerektiğinden habersizler. Cümlelerin sonu ama yol yaptılar, köprü yaptılar olarak tamamlanıyor ve bu sizi derinden üzebiliyor.\u003c/p\u003e\n\u003cp\u003eBir de Facebook grupları üzerinden ciddi bir bilgi paylaşımı var. İşin kötü tarafı ise konuşulan konuların bir kısmı kapağı buraya atmama yardımcı olun, diğer kısmı da sistemi kandırmaya yönelik konular. Bu kadar çok etik dışı konunun arasında ufak tefek güzel konular da çıkmıyor değil. Tiyatro, sinema, halı saha maçları için size eşlik edecek birilerini bulmak cidden zor değil.\u003c/p\u003e\n\u003ch3 id=\"uzun-lafın-kısası\"\u003eUzun lafın kısası\u003c/h3\u003e\n\u003cp\u003eYurt dışında yaşama kararınız karşısında alışkanlıklarınızdan vazgeçip tamamen gittiğiniz ülkenin size verdiği hayatı seçmek zorunda değilsiniz. Canınız istediğinde \u0026ldquo;şefim” diyerek sipariş verebileceğinizi bilmek insanı biraz olsun daha evinde hissettiriyor.\u003c/p\u003e\n\u003cp\u003eAlmanya görece daha sosyalist ve düzenli bir ülke. Kapitalizmden uzak duran bir ülkede yaşamak beni çok mutlu ediyor. Iphone 4s kullanıp yenisini almak için sebep bulamayan, gelir seviyesi gayet iyi olan bir çok insan var. Medeniyet olarak bizden önde oldukları çok nokta var.\u003c/p\u003e\n\u003cp\u003eTüm bu başlıkları alt alta koyduktan sonra Almanya’da yaşamayı denemek istedik. Geçiş sürecinde ve ilk bir senemizin sonuna doğru gelirken, genel olarak mutlu olduğumuzu ve hala bir geçiş sürecinde olduğumuzu hissediyoruz.\u003c/p\u003e\n\u003cp\u003eNerede yaşarsan yaşa, hiç bir zaman Türkiye’yi terk edemezsin. Çünkü arkadaşlıklar, dostların ve hayatının bir parçası hala oradadır. Her zaman o parçana yakında olmak insana ciddi bir huzur veriyor.\u003c/p\u003e\n"},{id:4,href:"/blog/2018/01/arad%C4%B1%C4%9F%C4%B1m%C4%B1z-i%C5%9Fi-bulabildik-mi/",title:"Aradığımız işi bulabildik mi?",description:null,content:"\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/looking-for-a-job.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eLooking for a job\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eGeçen hafta bir anket yapmıştım, sandık sonuçlarına göre bu haftanın konusu olarak iş arama süreçlerindeki ve genel iş hayatındaki dikkate değer bir kaç notumu paylaşmak istedim.\u003c/p\u003e\n\u003cp\u003eKariyer hedefimizi belirlerken yakın çevremizdeki insanlar ve internetten bulduğumuz tavsiyeler herkes için yol gösterici olacaktır. Tüm bu kaynakları tekrardan kendi fikir süzgeçinden geçirip tekrar değerlendirmekte fayda vardır.\u003c/p\u003e\n\u003cp\u003eGelelim yapılması gereken adımların neler olduğuna. Bu yazıda bahsedeceğim bir çok adım tabiki de kendi çalıştığım sektörü baz alarak değerlendirdiğim konulardır.\u003c/p\u003e\n\u003ch3 id=\"kendinizi-tanıyın\"\u003eKendinizi tanıyın\u003c/h3\u003e\n\u003cp\u003eTürkiye’deki insanların çok büyük bir kısmında bariz şekilde görünen en büyük sorun özgüven eksikliği ya da tersi olacak şekilde kendisine gereğinden fazla güvenilmesidir. Öncelikle kendimizi tanıyarak başlamalıyız, bir işe başvururken kendi hedeflerimize ne kadar yakın olduğuna göre karar vermemiz gerekiyor. Bu hedeflerin karar verilmesi konusunda daha detaylı olarak sonradan değinebilirim.\u003c/p\u003e\n\u003cp\u003eKariyerimizin ilk başlarında ne kadar çok ve farklı insan ile iletişimde olursak, bu kararın verilmesinde faydası olacaktır.\u003c/p\u003e\n\u003cp\u003eSon zamanlarda yurt dışında çalışmak için ciddi \u003ca href=\"https://medium.com/@salimkayabasi/gidenler-kalanlar-3ee484c3a56d\"\u003ebir göç dalgası\u003c/a\u003e yaşanıyor. Lütfen ama lütfen kendinizi gitmek istediğiniz ülkedeki insanlardan geri görmeyin. Son bir senedir Almanya’da o kadar farklı profilden insan ile tanıştım ki emin olun İstanbul’daki bilgi birikim kalitesi ciddi oranda buradan fazladır.\u003c/p\u003e\n\u003ch3 id=\"dünya-ile-iletişimde-kalın\"\u003eDünya ile iletişimde kalın\u003c/h3\u003e\n\u003cp\u003eYaptığımız işin tek bir ülke sınırları içerisinde olmadığını unutmayalım. Dış dünyaya karşı üç maymunu oynadığımız sürece sadece kendimizi kandırmış oluruz. Bu sebeple ilk yapılması gereken belki de lise dönemlerinden ya da daha öncesinden itibaren yabancı dil konusunda kendimizi geliştirmeye çalışmak.\u003c/p\u003e\n\u003cp\u003eGünlük pratik yapabilmek için yabancı dildeki içerikleri tüketmek, sosyal medyadan kendi kariyer hedefinize yakın insanlarla iletişimde olmak faydanıza olacaktır.\u003c/p\u003e\n\u003cp\u003eBir çoğu sonradan internetten izlenebilen etkinlikleri takip etmeye çalışın. Fırsatınız varsa gidip etkinlik alanındakı havayı tenefüs etmeye çalışın. Güncel teknolojileri yakından takip etmeye, en kötü birer \u0026ldquo;hello world” uygulaması çıkarmaya çalışın. Böylelikle o ilk incelemeyi yaptığınız teknolojiye ileride ihtiyacınız olduğunuzda ilk korku bariyerinizi aşmış olacaksınız. Tabiki de öğrendiklerinizi paylaşmayı unutmayın.\u003c/p\u003e\n\u003ch3 id=\"yurt-dışında-iş-ararken\"\u003eYurt dışında iş ararken\u003c/h3\u003e\n\u003cp\u003eÇoğu linkedin, xing, glassdoor, stack overflow gibi sitelerde ilanlar ile başlıyor bütün hikaye. İlanı ararken filtreleri çok katı tutmanıza gerek yoktur. Çoğu CV’nin okunmadığı gerçeğini herkes biliyor. Bu sebeple çok uzun CV yazmaya gerek yoktur. Aynı zamanda da başvuracağınız firmadaki kişinin hızlı bir şekilde görebilmesi için gereken kelimeleri vurgulamayı unutmayınız.\u003c/p\u003e\n\u003cp\u003eFirmaya doğrudan ulaşmak ya da head hunter üzerinden gitmek çok fark etmiyor. İnsan kaynaklarında çalışan insanların teknik başlıklarda yazdığınız detayları okurken sıkılacağı bir gerçektir.\u003c/p\u003e\n\u003cp\u003eBir çok firma PDF formatındaki CV’nizi görmek isteyecektir. Yeterince şansınız varsa başvurduğunuz takımdan birisi detaylı bir şekilde tüm yazıları okuyabilir.\u003c/p\u003e\n\u003cp\u003eYurt dışında insanlar sizin bireysel yeteneklerinizden çok ekibe katkınıza ve beraber çalışabilme ihtimaline dikkat ediyor. Bu sebeple dili akışkan konuşmanız, hata yapmaktan korkmamanız ve karşılaştığınız sorunlara nasıl çözümler getirdiğiniz ciddi oranda fark ediyor.\u003c/p\u003e\n\u003cp\u003eÜlkemizde Jr. ya da Sr. tanımladığımız başlıkların burada çok farklı olduğunu gördüm. Kısaca özetlemek gerekirse, kabul edilen tanım şu şekildedir. Eğer tek başınıza bir projeyi sıfırdan alıp bir yere getirebilecek kadar birikim ve iletişim yeteneğiniz varsa sizin için artık \u003cem\u003eSenior\u003c/em\u003e diyebiliriz. Bunun dışındaki her durumda kaç yıllık tecrübeniz olduğundan bağımsız, hepiniz Senior adayısınız.\u003c/p\u003e\n\u003cp\u003eBir de \u003cem\u003eSenior Lead\u003c/em\u003e olarak adlandırılan bir profil daha var, burada da beklenti biraz daha takım sorumluluğu alıp, diğer takımlar ile de çalışabilecek durumda demektir. Bu tanımın takım lideri gibi illa ki bir takımı yönetmeniz gerekmiyor. Bir takım içerisinde birden fazla \u003cem\u003eSenior Lead\u003c/em\u003e olabiliyor, bu insanlar insiyatif alarak takımı yönlendirebilir manasına geliyor.\u003c/p\u003e\n\u003cp\u003eKod kalitesi de diğer önemli noktalardan birisidir. İlk defa karşılaştığınız bir teknoloji için bile olsa teknik mülakatta bazı anahtar cümlecikler vardır. Kodun test edilebilir olması, tekrar kullanılabilir kod yazmak, kodunuzun canlı ortama gidene kadarki sürecine dair bir kaç cümle söylemek gibi örnekler çok faydalıdır.\u003c/p\u003e\n\u003ch3 id=\"yurt-içinde-iş-ararken\"\u003eYurt içinde iş ararken\u003c/h3\u003e\n\u003cp\u003eTürk iş arama sitelerindeki ilanların kalitesizliği aslında sektöre de bir ayna tutmaktadır. Ayrıca sadece lokal markete hizmet veren bir sitede CV’nizi hazırlamanın bir faydası yoktur. Bu sebeple yukarıda saydığım daha global sitelerde güncel bir CV’ye sahip olmak sizin için daha faydalıdır.\u003c/p\u003e\n\u003cp\u003eTürkiye’de işler biraz değişiyor. Eğer gerçekten çalışmak istediğiniz bir firma ve proje bulduysanız, işin kalitesini ve ekip ruhunu öne çıkaran cümleler işinize yarayacaktır. Ama işler pek yolunda gitmediği bir dönemde herhangi bir iş olması sizin için şimdilik yeterliyse; bazı cümleler ile işe alım sürecinizi hızlandırabilirsiniz.\u003c/p\u003e\n\u003cp\u003eEğer görüşmeye teknik birileri girdiyse, firmanın kaliteye önem verdiğini düşünebiliriz. Eğer görüşmeye yönetici seviyesinden birileri girdiyse, derdimizin kaliteden çok sayı olduğunu görmek zor değildir.\u003c/p\u003e\n\u003cp\u003eYöneticiyi etkilemek için, kendinize yeterinden fazla güvenip bunu da hissettirin. Çok da abartıp karşınızdaki kişiyi geçmeyecek kadar yükselmeniz gerekiyor. Ara sıra karşınızdaki insanın egosunu okşayacak şekilde cümleler kurup, onun da kendini değerli hissetmesine izin verin.\u003c/p\u003e\n\u003cp\u003eHadi bunlar olmadı diyelim, çok çalışkan olduğunuzu mesai konusunda sorun yapmadığınızı söyleyerek devam edin. Önceki iş yerinizden neden ayrılıyorsunuz sorusuna basit olarak görüştüğünüz firmayı överek cevap verin asla o an için çalıştığınız firmayı kötülemeyin.\u003c/p\u003e\n\u003cp\u003eSektörün ne kadar küçük olduğuna inanamazsınız, ayrıca etik kuralların hiçe sayıldığı da bariz bir gerçektir. İnsanlar açıktan en son aldığınız maaşı sorabilirler. Söylemeseniz de bir şekilde bu rakamı gerçekten öğrenebilirler. Kesinlikle aramızda kalacak diye söz verilen herşeyin nasıl da herkes tarafından duyulabileğine inanamazsınız.\u003c/p\u003e\n\u003cp\u003eGörüşme sırasında samimi davranmaya özen gösterirseniz, görüşmede siz de dahil herkes rahat davranacaktır. Bazı beklemediğiniz itirafları duyup karar vermeniz konusunda yardımcı olacak ip uçlarını elde edebilirsiniz.\u003c/p\u003e\n\u003ch3 id=\"startup-kültürü-nedir\"\u003eStartup kültürü nedir?\u003c/h3\u003e\n\u003cp\u003eAslında küçük takımların daha hızlı adımlar atarak, katma değeri çok olan ürünleri daha özgür çıkarabilmesi için olan bir kültürdür. Tabiki de tek başına takım ve fikir ile olmuyor. Bu sebeple işin içine parası olan birilerinin de dahil olması gerekiyor.\u003c/p\u003e\n\u003cp\u003eÜlkemizde yakın zamanda popüler hale gelen startup şirketler ve hackathon etkinliklerinin giderek anlam kaybetmesi sonrasında sektöre de zararlı olabilecek sonuçlar ortaya çıkmıştır.\u003c/p\u003e\n\u003cp\u003eBazı startuplar aslında paramız ve fikrimiz var ama bu iş nasıl yapılıyor bilmiyoruz. Şuraya kafası basan bir kaç çocuk alalım ve sabaha kadar kod yazdıralım, yazılım işinde çok para varmış diye başlıyor.\u003c/p\u003e\n\u003cp\u003eSektörün gereksinimlerine dair hiçbir fikri olmayan sadece yatıracağı 100K TL ile 1M$ kazanmayı hedefleyen bazı firmalar türemiş durumda. Çoğu da daha bir ay önce adını duyduğu bugün biraz popüler olan başka bir ürünü kopyalamak üzerine gidiyor. Benzer şekilde hackathon etkinlikleri de bir mekan tutalım pizza verelim ve milletin fikirlerini çalalım durumuna geliyor.\u003c/p\u003e\n\u003cp\u003eSektör genel olarak, birbirine kazık atarak değil destek olarak gelişir.\u003c/p\u003e\n\u003cp\u003eBu tarz emek ve fikir sömürüsü yapan etkinliklerden ve firmalarda uzak durmak koşulu ile startup kültürünü gerçekten benimsemiş firmalarda çalışmak çoğu zaman kariyerinizde ciddi bir öğrenme dönemi olacaktır.\u003c/p\u003e\n\u003cp\u003eTürkiye’de daha kat edilecek çok adım varken kaliteyi korumak yine biz çalışanların elindedir. Yakın zamanda okuduğum bir yazıda 2017 içerisinde startup yatırım hacmi Türkiye’de \u003ca href=\"http://www.milliyet.com.tr/serkan-unsal/turkiye-girisim-ekosistemi-bir-ust-teknoloji-2593810/\"\u003e100M$ seviyesine\u003c/a\u003e yükseldiğini ve İstanbul’un parlayan yıldız olduğundan bahsediliyordu. Şu an çalıştığım firmanın son turunda \u003ca href=\"https://webrazzi.com/2018/01/15/turk-asilli-girisimcinin-kuruculari-arasinda-oldugu-auto1-coma-460-milyon-euro-yatirim-geldi/\"\u003ealdığı yatırım\u003c/a\u003e ise 406M€’dur.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/turkiye-startups.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eSerkan Ünsal — Türkiye Girişim Ekosistemi bir üst lige çıktı! (milliyet.com.tr)\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eSektörün ve bizlerin hala gelişmeye ve geliştirmeye ihtiyacı vardır.\u003c/p\u003e\n\u003ch3 id=\"kendimizi-göstermeliyiz\"\u003eKendimizi göstermeliyiz\u003c/h3\u003e\n\u003cp\u003eYukarıda bahsettiğim görüşme sırasında yapılabilecek bir kaç minik taktiğin faydaları ve zararlarından bahsettim. Bunların öncesinde o görüşmeyi alabilmek için kendimizi göstermemiz lazım.\u003c/p\u003e\n\u003cp\u003eÖncelikle kendimize güzel bir mail adresi alarak başlamalıyız. Bu da dikkatimi çeken önemli hatalardan birisidir. Artık hotmail kullanan kalmadığını düşünüyorum ama hala aramızda twitter’daki kullanıcı isimlerine benzeyen CrazyBoy06 gibi hesaplar kullanan var. Çok ciddi denk geliyorum :) Lütfen ama plaka kodu ya da doğum tarihi kullanmaktan vazgeçin. Ayrıca birden fazla mail hesabı kullanmak da dönüşler için iletişimi zora sokuyor.\u003c/p\u003e\n\u003cp\u003eOnline bir CV kullanmak da faydalı olacatır. Burada bahsettiğim bir linkedin hesabınız olmasının yanı sıra PDF formatında hazırlanmış CV’nin de Google Drive gibi kolayca erişilebilir olmasıdır.\u003c/p\u003e\n\u003cp\u003eKendi sesinizi duyurmaya çalışın, medium’da yazı yazmak ya da github üzerinde çalışamalarınızı yayınlamak da en güzel örneklerdir.\u003c/p\u003e\n\u003cp\u003eİlk başlarda bahsettiğim konuya dönersek. Global dünyadan kopmamak için takip ettiğiniz alandaki yeni bir kütüphane ya da teknolojiyi oturup ufak bir inceledikten sonra bunun için bir örnek kod ile birlikte bir yazı yazmak çok faydalı olacaktır.\u003c/p\u003e\n\u003cp\u003eHem CV’nize sığması imkansız olan içeriklerin de sizin kariyerinizde birer birikim olarak hala erişilebilir olmasını sağlayacaktır.\u003c/p\u003e\n\u003cp\u003eBütün içerik ve sosyal medya hesaplarınızı ortak bir yerde toplayabilirsiniz. Buradaki örnek github pages ile tek bir HTML sayfası da olabilir ya da about.me gibi servisleri kullanmak da olabilir.\u003c/p\u003e\n\u003cp\u003eBurada medium, twitter, linkedin ve github gibi profillerinizi listeleyip, CV’nizde tek bir link verebilirsiniz.\u003c/p\u003e\n\u003cp\u003eŞirketinizde github kullanılıyorsa ya da kendiniz için private repo kullanıyorsanız lütfen bunları da paylaşmayı unutmayın.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/private-contributions.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eprivate contributions\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eLinkedin üzerinde projelerinizi tek tek açıklamayı ve varsa çalıştığınız insanları da projeye katılımcı olarak göstermeyi unutmayın. Unutmayın, bireysellik değil takım oyunu size kazandırır.\u003c/p\u003e\n\u003cp\u003eÖğrenci bile olsanız; gidebildiğiniz kadar etkinliğe gitmeye özen gösterin ki hem sosyal çevrenizde yardım alabileceğiniz daha çok insan olur hem kendize rol model alabileceğiniz insanlarla tanışmış olursunuz. İlk akla gelen topluluklar için Yusuf’un \u003ca href=\"https://github.com/yusufcakmak/topluluk-rehberi\"\u003ehazırladığını listeye\u003c/a\u003e bakabilirsiniz.\u003c/p\u003e\n\u003ch3 id=\"toparlamak-gerekirse\"\u003eToparlamak gerekirse;\u003c/h3\u003e\n\u003cp\u003eBir işi ararken önce kendinizi tanıyın ve buna göre bir iş bakın. Firma ya da proje seçerken emeğinizin sömürülmesine kesinlikle izin vermeyin. Tek başına iki sayfalık bir CV sizi tamamıyla anlatamaz ama destekleyici içerikler her zaman faydalıdır.\u003c/p\u003e\n\u003cp\u003eYaşadığınız şehir ve dünyada neler oluyor bunları takip edip farklarını görüp siz nasıl kapatabilirsiniz buna bakmaya çalışın. Böylelikle sektöre çok yakın birisi olacaksınız ve bir yerden sonra iş aramak ya da bulmak yerine gelen tekliflerde değerlendirme noktasına geçmiş olacaksınız.\u003c/p\u003e\n"},{id:5,href:"/blog/2018/01/t%C3%BCketiliyoruz/",title:"Tüketiliyoruz",description:null,content:"\u003cp\u003eGeçtiğimiz haftalarda eski bir arkadaşım ile sohbet ederken nazarımda çok ağır bir yorumunu işittim. Toplum olarak artık herhangi bir konuda hiç birşey üretmiyoruz. Burada bahsedilen üretimin tarım ürünü ya da bir yazılım olması zorunlu değil.\u003c/p\u003e\n\u003cp\u003eUzun zamandır, tek taraflı bir bilgi akışı içerisinde olduğumu ve bunu değiştirmek için bir adımda bulunmadığımı farketmemi sağladı bu sohbet.\u003c/p\u003e\n\u003cp\u003ePeki neler üretebiliriz, üretmemek neden zararlıdır? Tüketmek de bu işin bir parçası değil mi? Sadece tüketiyor olmak çok mu kötü birşey acaba?\u003c/p\u003e\n\u003cp\u003eÖncelikle tüketim nasıl davranış biçimidir, bunu değerlendirmek gerekiyor. Tüketim genel olarak bireyin pasif davranış geliştirmesine sebep olmaktadır. Belirli bir davranışın belirli bir döngüde tekrarlanmasından sonra teknik olarak bu davranış alışlanlık haline gelmektedir.\u003c/p\u003e\n\u003cp\u003eÖrneklemek gerekirse; sosyal medya kullanımı, televizyon izlemek, youtube/netflix üzerinden video izlemek gibi davranışlar aslında birey olarak genelde etkileşime girmemize gerek kalmadığımız aktivitelerdir.\u003c/p\u003e\n\u003cp\u003eAcaba yorumda bir hata mı oldu diye tekrar düşündüğümüzde; sosyal mecralar kullanıcı içeriklerinden oluşuyor, youtube’da kullanıcı yorumları dolu diyebilirsiniz. Biraz detaylı baktığımızda içeriklerin çoğunun başka bir kaynaktan (ç)alıntı olması, üretilen yorumların aslında bir katma değerinin olmaması ve artık yazı yazmak yerine sadece emojilerle kendimizi ifade etme çabamız görülüyor.\u003c/p\u003e\n\u003cp\u003e\u0026ldquo;Kanalımıza abone olmayı ve beğenmeyi unutmayın.” çünkü bundan başka yapabileceğiniz başka bir etkileşim yoktur. Pardon yorum da yapabilirsiniz ama çok fazla yorum geliyor biz onları okuyamıyoruz kusura bakmayın.\u003c/p\u003e\n\u003cp\u003eKendimizi bu içeriğin bir parçası halinde hissettiğimiz yalanı da kullanıcılar tarafında o kadar kabul görmüş durumdadır ve artık alışkanlık haline gelmiştir. Sürekli olarak acaba yeni bir içerik var mı diye gün içerisinde defalarca instagram twitter açıp açıp bakıyoruz. Çok nadiren içerik üretip biz de katkıda bulunuyoruz.\u003c/p\u003e\n\u003cp\u003eTüketirken aslında üretim yeteneğimizin tüketildiğini bariz şekilde kabul etmiş oluyoruz. Alışveriş çılğınlıkları, reklam kampanyaları gibi konu derinleştirilebilir. Teknolojiyi yakından takip ediyor olmak elma logolu telefonun son modelini alıp arkadaş ortamında hava atmakla olmuyor, ne yazık ki.\u003c/p\u003e\n\u003cp\u003eTamam tüketime karşın üretim yapmak istersek nereden başlamalıyız? Yazının başında bahsettiğim arkadaşımın da verdiği örnekleri de eklersek akla gelen bir kaç örnek şudur;\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eBir kitabı okuduktan sonra kısa da olsa bir yorumlamak ve bir yerde paylaşmak\u003c/li\u003e\n\u003cli\u003eSinemada filmi izledikten sonra biraz daha detaylı bir inceleme yazısı yazmak\u003c/li\u003e\n\u003cli\u003eGezilen görülen şehirlerdeki tecrübeleri paylaşmak\u003c/li\u003e\n\u003cli\u003eHayat tercübelerini paylaşmak\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eve en önemlisi bu davranışı alışkanlık haline getirmek için düzenli olarak yapmak. Aslında anlatmaya çalıştığım şeyin şu an \u003ccode\u003efenomen\u003c/code\u003e olarak bildiğiniz bir çok insanın birbirini tekrar ettiği sosyal medyalarda sizlerinde var olmasıdır. Buradan çıkacak sonuç tepkiye tepki videosu çekip bize abone olun diye biten videolar çekmek değildir. Sadece tükettiğiniz bir içeriğin üzerine farklı düşünerek neler ekleyebiliriz düşüncesi ile yaklaşmaktır.\u003c/p\u003e\n\u003cp\u003eBir noktadan sonra yaptığınız diğer tüm gözlemleri de paylaşarak aslında kendinize göre bir yorum farkı koymuş olacaksınız. Aslında bu felsefeden yola çıkarak düzenli olarak yazı yazmaya karar verdim.\u003c/p\u003e\n\u003cp\u003eHerkes kendini en iyi ifade edebileceği şekilde bir yol bulup devam etmeli. Üretim yapma davranışını kaybetmiş bireylerden oluşan bir toplumun da kültürel ve sosyal olarak da üretime katkısı olması beklenemez. Hiç kimsenin ilk etapta ülkeyi yüksek medeniyet seviyesine çıkaracak içerikler üretmesini beklemek doğru değildir ama bir yerden başlamak en azından sadece tüketmek ve tüketilmekten daha iyidir.\u003c/p\u003e\n\u003cp\u003eKitapları sadece popüler olduğu için okumayın. Her yaz tatilinde ayaklarını da kareye sığdırarak fotoğraf çekmek zorunda değiliz. Slime ya da makyaj videosu hayatınızdan kaç dakika çalıyor acaba? Yemeğin fotoğraflarını çekip instagram’a atmak kolay ya peki fotoğraf çekim tekniklerini hiç incelediniz mi? İki saati aşkın süre boyunca sadece ajitasyon aktaran ve melankoli içeren dizileri izlemek yerine görüntü ve içerik kalitesi olan daha başarılı yapımları tercih ediniz. Üreten insanları yapıcı cümlelerle eleştirmeye çalışın çünkü en azından sadece tüketmiyor ve üretiyorlar. Etrafınızda dönen dünyada kendinize bir rol yazmadığınız sürece elinize gelen senaryodaki figüranlıktan ileriye gitmek zor olacaktır.\u003c/p\u003e\n\u003cp\u003eSon zamanlarda belki de sık sık duyduğunuz olmazsa olmaz diye düşündüğünüz kinoa salatası ya da san sebastian cheesecake olmadan da hayat eskisi gibi akıyordu. Farklı bakış açıları kazanmayı öğrenmediğimiz sürece önümüze gelen yemeği yemek dışında bir alternatifimiz yoktur.\u003c/p\u003e\n\u003cp\u003eLütfen ama lütfen, tükettiğiniz içeriğin kalitesini tekrar gözden geçirip iyileştirmek adına bir karar alın. Size katma değeri olanları tercih etmeye çalışın ve en önemlisi popüler olması dışında bir argümanı olmayan tüm başlıklardan uzakta durmaya çalışın. Youtube özelinde düşünürsek belki de \u0026ldquo;\u003ca href=\"https://www.youtube.com/watch?v=5guS-TtJq9Y\"\u003eYouTube’un dibi hareketi\u003c/a\u003e”ni bir incelemenizde fayda vardır.\u003c/p\u003e\n\u003cp\u003eTüketilmemek için bazı önemli noktanın altını çizmek istedim. Öncelikle tükettiğiniz içeriğin kalitesini arttırmak ve sonrasında da üretecek bir fikir bulmak. Üretmek için düşünmeye, düşünmek için okumaya ve kendimizi geliştirmeye ihtiyacımız vardır. En önemlisi bu davranışı alışkanlık haline getirmek zorundayız. Başka türlü, tüketilen bir toplumdan üreten bir topluma giden başka bir yol yoktur. Diğer ülkelerde insanlar tiyatro, spor, müzik gibi kültürel aktivitelerden bahsederken biz hala ünlüler gönüllüler adasını tartışmaktan ileri gidemiyor olabiliriz.\u003c/p\u003e\n"},{id:6,href:"/blog/2018/01/gidenler-kalanlar/",title:"Gidenler, kalanlar",description:null,content:"\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/kavimler-gocu.jpg\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eKavimler göçünü başlatan insan\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cblockquote class=\"twitter-tweet\"\u003e\u003cp lang=\"tr\" dir=\"ltr\"\u003eülkedeki göç dalgası ile ilgili dün akşam yazdığım yazı için minik bir anket\u003cbr\u003e\u003cbr\u003e“Gidenler, kalanlar” \u003ca href=\"https://twitter.com/salimkayabasi?ref_src=twsrc%5Etfw\"\u003e@salimkayabasi\u003c/a\u003e \u003ca href=\"https://t.co/8YbeK0Ya3J\"\u003ehttps://t.co/8YbeK0Ya3J\u003c/a\u003e\u003c/p\u003e\u0026mdash; Salim KAYABAŞI (@salimkayabasi) \u003ca href=\"https://twitter.com/salimkayabasi/status/949933134606491649?ref_src=twsrc%5Etfw\"\u003eJanuary 7, 2018\u003c/a\u003e\u003c/blockquote\u003e\n\u003cscript async src=\"https://platform.twitter.com/widgets.js\" charset=\"utf-8\"\u003e\u003c/script\u003e\n\n\u003cp\u003eSon zamanlarda giderek popüler hale gelen bir konu olduğunu düşündüğüm için benim de belki bir kaç kelime ile katkı yapmamın zamanı geldi gibi. Peki nedir bu Türkiye yaşanan göç dalgası?\u003c/p\u003e\n\u003cp\u003eGidenler kadar kalanların da bu göç dalgasının bir parçası olduğunu da bir kenara not düşerek başlamak gerekiyor. Konu etrafında bir kaç alt başlıkta toparlamaya çalıştığım bir yazı olsun istiyorum. Buradaki bir çok tespit belki öznel yorumlar olacaktır ama bir kısmı da yakın çevremden aldığım geri bildirimlere dayanmaktadır.\u003c/p\u003e\n\u003ch1 id=\"nedenler\"\u003eNedenler\u003c/h1\u003e\n\u003cp\u003eTemelinde en baskın görünen neden geleceğe karşı olan umutsuzluk durumu. Peki insanlar neden bu kadar umutsuz hissediyorlar? Tek başına belirgin bir cevabı olmamakla birlikte, tahmin edilenin aksine tek sebebi siyasi iktidar değildir.\u003c/p\u003e\n\u003cp\u003eSiyasetin tabiki de toplum üzerindeki etkisi yoğun şekilde hissedilmektedir. Giden insanların genel olarak şikayet ettiği problemlerin başında toplumda giderek artan saygısızlık ve çocuklar için daha güvenli bir yaşam alanı arayışı olduğunu düşünüyorum.\u003c/p\u003e\n\u003ch1 id=\"mesleki-sebepler\"\u003eMesleki sebepler\u003c/h1\u003e\n\u003cp\u003eKendi çalıştığım sektörde yıllardır şikayet ettiğim konu başlıklarından birisiydi mesleki saygısızlık. Bunun giderek arttığı çok bariz bir şekilde görülmektedir. Bilginin ve tecrübenin yerine kartvizitine göre lafının dinlenmesi durumu insanları ciddi oranda yıpratmaktadır.\u003c/p\u003e\n\u003cp\u003eYıllar önce şu tarz lafları çevremizden çok sık duyardım. Bizim meslek bu ülkede bir öğretmen bir doktor kadar değer görmüyor gibisinden serzenişler vardı. Ama biraz dikkatli bakılırsa artık hiç bir mesleğin değeri pek kalmadı gibi. Artık hiç bir doktor, öğretmen, avukat ya da savcıya mesleki donanımından dolayı değil sahip olduğu çevresi ya da tanıdıkları başka başka insanlardan dolayı saygı duyulduğuna şahit olunuyor.\u003c/p\u003e\n\u003cp\u003eAslında aynı ayıbı biz de yıllardır çevremizdeki diğer insanlara yapıyorduk. Bugüne kadar bir tehsisat ustasına, tezgatara ya da garsona mesleği için saygı duymadığımızı fark ettim. Bu konuya daha detaylı olarak itiraflar başlığı altında değinmek istiyorum.\u003c/p\u003e\n\u003cp\u003eAslında hayaller \u003ca href=\"https://tr.wikipedia.org/wiki/Meritokrasi\"\u003emeritokrasi\u003c/a\u003e ama gerçekler yandaşçılık olmasıdır. Yaptığımız işten hiç bir şekilde saygı görmüyoruz, birini zengin etmediğiniz sürece gerçekten üretime katknınız olmuyor. Bunun sonucunda da yoksunluk hissiyatı artıyor ve insanlar kendilerini toplumdan koparılmış hissetmeye başlıyor. Belki de \u003ca href=\"https://en.wikipedia.org/wiki/IKEA_effect\"\u003eIKEA etkisi\u003c/a\u003e ile açıklabilir bir durum olabilir.\u003c/p\u003e\n\u003ch1 id=\"kimler\"\u003eKimler\u003c/h1\u003e\n\u003cp\u003eGidenlerin kim olduğuna dair ciddi bir araştırma yapılmış değil. Yerli ve yabancı kaynaklarda giderek artan bir şekilde bu durumlar incelenmektedir. Akademik olarak da bir kaç çalışma yapıldığını duymuştum ama herhangi bir makale bulamadım henüz.\u003c/p\u003e\n\u003cp\u003ePeki bu göç dalgasında kimler ön planda yer almaktadır. Bildirimlere dayalı şekilde içerik toplayan \u003ca href=\"https://buyukgoc.github.io/map.html\"\u003ebuyukgoc.org\u003c/a\u003e sayfasından rahatça rakamlara bakılabilir. Sitenin bilinirliği genellikle yazılım dünyasındaki insanlar tarafında olduğu için elde edilen bilgilerin de genelde bu sektörden olduğu görülüyor.\u003c/p\u003e\n\u003cp\u003eZamanında Gülse Birsel’in de \u003ca href=\"http://www.hurriyet.com.tr/yazarlar/gulse-birsel/insanlar-neden-korkuyor-40265748\"\u003eyazısında\u003c/a\u003e vurguladığı kesimin de aşağı yukarı görece eğitimli ve gelir seviyesi olarak maddi sıkıntısı olmayan insanlar olduğu görünmektedir.\u003c/p\u003e\n\u003cp\u003eGözlemlerime dayanarak genelde göçenlerin 25–35 yaş aralığında ve evli insanların yoğunluklu olduğu görülmektedir. Tabiki de öznel bir çıkarımdır ama tek başına hiç bilmediğin bir ülkede yaşamak korkutucu geldiğinden buna cesaret edenlerin çoğunluğu evli insanlardır. Evli insanların gelecek kaygılarından birisi de kendilerinden çok çocuklarının geleceğinden endişe etmesi baskın olabilir.\u003c/p\u003e\n\u003cp\u003eEvini arabasını satıp, gittiği ülkede belki altı ay belki bir sene kadar işsiz kalmayı kabul edecek şekilde giden insanların sayısı da az değildir. Genelde eşlerden birisi bir şekilde vize işlerini halledince gidelim sonra hallederiz kalanını şeklinde gelişiyor.\u003c/p\u003e\n\u003cp\u003eBu insanların bu kadar gözünü karartıp gidebilmesi için geçerli olan bariz durumlardan birisi de insanların artık toplumdan giderek uzaklaşmış olmasıdır. Toplumdan ayrışan bir kesimin ülkeyi terk etmesinden ziyade, toplum içerisinde herkesin diğer herkesten ayrışması sonrasında gidebilecek olanların şansını deneme çabasıdır.\u003c/p\u003e\n\u003cp\u003eGidenlerin arkada bıraktığı sosyal ortamları, arkadaş ortamı ve aile bağları kadar gittikleri yerlerdeki çevre kurma çabaları da hiç kolay olmamaktadır.\u003c/p\u003e\n\u003cp\u003eSon zamanlarda bu göçün sadece orta yaş grubunda olmadığınıda gördüm.\u003c/p\u003e\n\u003cp\u003eSektörde işi gücü yerinde olup, yeni bir hayata başlamak isteyenler\u003c/p\u003e\n\u003cp\u003eYüksek lisans, doktora için gelip artık geri dönmek istemeyenler\u003c/p\u003e\n\u003cp\u003eLisans eğitimini yurt dışında almak isteyenler\u003c/p\u003e\n\u003cp\u003eve artık lise eğitimi için bile yurt dışını düşünenler\u003c/p\u003e\n\u003cp\u003eListe giderek yaş ortalamasın düştüğünü göstermektedir.\u003c/p\u003e\n\u003ch1 id=\"korkular\"\u003eKorkular\u003c/h1\u003e\n\u003cp\u003eİnsanların korkuları var ve artık bunları saklamaktan çekinmiyorlar. Çünkü bireylerin tek başına aşabileceği korkuların yerini toplumsal korkular almış durumdadır. Gidilmek istenen ülkede genel olarak bu tarz toplumsal korkuların olmadığı ve bunların yerini adaptasyona bağlı korkuların yer aldığı görülmektedir. Korkular o kadar ayyuka çıkmış durumdadır ki; insanlar herhangi bir konuda fikir beyan ederken ismini vermekten çekinmektedir.\u003c/p\u003e\n\u003cp\u003eGitmeden önceki korkularımız;\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eBazı düşünceleri yüksek sesle dile getirince hiç alakası olmadığı halde malum terör örgütü zanlısı ilan edilmek\u003c/li\u003e\n\u003cli\u003eBir hakaret davasında sanık olmak\u003c/li\u003e\n\u003cli\u003eBilime dayalı eğitim yerine dine dayalı eğitime maruz kalma korkusu\u003c/li\u003e\n\u003cli\u003eAcaba bir yerde tacize uğrar mıyım?\u003c/li\u003e\n\u003cli\u003eBugün sokakta kaç kadın katledildi acaba?\u003c/li\u003e\n\u003cli\u003eİş hayatındaki cinsel ayrımcılığın son kurbanı kim oldu?\u003c/li\u003e\n\u003cli\u003eAcaba alacağım ikinci el araba hakkında kim bilir ne dolandırıcılıklar yapıldı?\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eGidenlerin korkuları;\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eArkada bıraktığım arkadaşlarım, ailem ne olacak?\u003c/li\u003e\n\u003cli\u003eYeni bir ülkede kurallar, bürokrasi nasıldır?\u003c/li\u003e\n\u003cli\u003eDil problemleri\u003c/li\u003e\n\u003cli\u003eYeniden inşa edilmeye çalışılan sosyal ortam\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eToplumun üzerine ağır bir şekilde çöken bir korku havası vardır ve kim kime güçlü olduğunu hissettirirse onun kazandığı bir dönemdeyiz. Aslında gelişmiş toplumların bir çoğunda korku yerine sevgi ve saygının kazandığını görmekteyiz.\u003c/p\u003e\n\u003cp\u003eBu bana biraz eski de olsa \u003ca href=\"http://www.imdb.com/title/tt0198781/\"\u003eMonsters, Inc.\u003c/a\u003e filmini hatırlatıyor. Kısaca bahsetmek gerekiyorsa; korkular üzerinden geçinen bir ekosistemin aslında mutluluk ile de başarı sağlanabileceğini anlamasını anlatmaktadır.\u003c/p\u003e\n\u003ch1 id=\"mutlu-musun\"\u003eMutlu musun?\u003c/h1\u003e\n\u003cp\u003eGöçün algılanmasında farklı kesimlerde farklı yorumlar bulunmaktadır. Ekşisözlük’te \u003ca href=\"https://eksisozluk.com/turkiyeden-siktir-olup-gitmek--3843083\"\u003etürkiye’den siktir olup gitmek\u003c/a\u003e başlığında bu tarz yorum farklılıkları çok bariz bir şekilde görülmektedir.\u003c/p\u003e\n\u003cp\u003eGitmek isteyene neden gitmek istediğini sorup ve bir çıkarım yapan bugüne kadar net bir şekilde çıkmadı. Gidenlere edilen ağır hakaretler ve gidenlerin yaptığı savunmaları içeren bu başlıktan çok güzel analizler çıkarılabilir.\u003c/p\u003e\n\u003cp\u003e\u0026ldquo;Ya sev ya terket” ya da \u0026ldquo;bi siktir git” türevleri ithamlar, gidenlerin arkasından el sallayanların bir kısmının da gitmek için yanıp tutuştuğu ama beceremeyip duruma nefretini kustukları ezbere laflar halini almıştır. Genelleme olmakla birlikte açıktan itiraf edip, ben gidemiyorum onlar gidiyorlarsa \u0026ldquo;siktirip gitsin”ler şeklinde dile getirildiğini gözlemledim.\u003c/p\u003e\n\u003cp\u003ePeki gidenler mutlu mu? Çoğunlukla net bir şekilde evet diyebilen yoktur. Hemen hemen herkesin ağzındaki cümlenin devamı \u0026ldquo;ama” diyerek bağlanıyor. Aslında bu durum insanların gitme konusunda asıl argümanlarının mutlu olmak değil umutlu olmak olduğu anlaşılıyor.\u003c/p\u003e\n\u003cp\u003eBu yüzden gittin de mutlu oldun mu? Gittiğin yerde arkadaşlık yok, bir sohbet ortamı yok, aradığın damak tadını bulamazsın şeklinde gelen mahalle baskına verilebilecek en kısa ve net cevap sanırım geleceğe karşı umutlu bakıyorum demek olabilir.\u003c/p\u003e\n\u003cp\u003eEvet bunu kabul edelim, bir çoğunluğu ekonomik ya da siyasi gerekçelerden değil de gelecek kaygılarından dolayı giden bu insanların çoğunluğu net bir şekilde mutluyum dememektedir. En azından umutlular diyebiliriz.\u003c/p\u003e\n\u003cp\u003ePeki asıl bu yazıyı okuyan ve göçün diğer parçası olanlar mutlu mu? Gitmemeyi tercih edenler ya da kalanlar diyebiliriz, sınıflandırmaya karşı olduğum bu durumun ülkemizin genel görünüşü itibariyle çok da parlak durumda olmadığını gösteriyor. Ülkeden giden de kalan da mutsuzken bu nefretin körüklenmesinin hiç kimseye faydası olmadığı çok barizdir.\u003c/p\u003e\n\u003ch1 id=\"izlenesiler\"\u003eizlenesiler\u003c/h1\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href=\"https://www.youtube.com/watch?v=WNWQO70amq4\"\u003eSeküler Göç\u003c/a\u003e - 140journos\u003c/li\u003e\n\u003cli\u003e\u003ca href=\"https://www.youtube.com/watch?v=QDZFCIf9EUc\u0026amp;list=PLSmDhZEqbQq3oPtgrZv7xOLmYdKP5KVZq\"\u003eGöç Yolları\u003c/a\u003e - Cüneyt Özdemir\u003c/li\u003e\n\u003cli\u003e\u003ca href=\"https://www.youtube.com/watch?v=1c5Guy9EGfY\"\u003eGurbetçi Yazılımcılar\u003c/a\u003e - Doğukan Güven Nomak\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch1 id=\"tldr\"\u003etl;dr\u003c/h1\u003e\n\u003cp\u003eBüyük Göç: Toplumdan koparılmış ya da kopmuş insanların geleceğine dair hissettiği bazı endişelerininden dolayı almış olduğu bireysel kararların bir topluluk hareketi haline gelmesi.\u003c/p\u003e\n\u003cp\u003eOluşan bu hareketin toplumdaki ayrışmayı daha da körüklediği gözlenmektedir. Gidenlerin büyük bir kısmı görece daha kötü ekonomik şartlarda, herşeye sıfırdan başlamayı göze alarak ve hatta mutlu olamayacağını en baştan kabul ederek ama geleceğe umutla bakma çabasındadır.\u003c/p\u003e\n"},{id:7,href:"/blog/2017/11/starting-with-hugo/",title:"Starting with Hugo",description:null,content:"\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/hugo.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eHugo\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eAccording to one of my own researches, I’ve found \u003ca href=\"https://gohugo.io/\"\u003eHugo\u003c/a\u003e which is static site generator for your blog page.\u003c/p\u003e\n\u003cp\u003eWith this article, I wanted to show you how I created a very basic static web page with using Hugo.\u003c/p\u003e\n\u003ch3 id=\"on-local\"\u003eOn Local\u003c/h3\u003e\n\u003cp\u003eThe project itself is written with \u003ca href=\"https://golang.org/\"\u003eGo\u003c/a\u003e so if you are Go developer feel free to compile it with using its source.\u003c/p\u003e\n\u003cp\u003eIf not, there are some binary files for your OS and other alternative ways to install it, please check \u003ca href=\"https://gohugo.io/getting-started/installing/#quick-install\"\u003ethis page\u003c/a\u003e.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-shell\" data-lang=\"shell\"\u003ebrew install hugo\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eAfter installing Hugo, you will be able to work with it on your terminal.\u003c/p\u003e\n\u003cp\u003eJust create your new site\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-shell\" data-lang=\"shell\"\u003ehugo new site my-blog\n\u003cspan style=\"color:#0087ff\"\u003ecd\u003c/span\u003e my-blog\nhugo server\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eThat could be very clear page as your home page because we have no theme or any content right now. Please check \u003ca href=\"http://localhost:1313/\"\u003ehttp://localhost:1313/\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eAdding our new theme, I’ve picked Hyde Y theme from \u003ca href=\"https://themes.gohugo.io/\"\u003eHugo Theme Gallery\u003c/a\u003e.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-shell\" data-lang=\"shell\"\u003egit clone https://github.com/enten/hyde-y.git themes/hyde-y\nrm -rf themes/hyde-y/.git\n\u003cspan style=\"color:#0087ff\"\u003eecho\u003c/span\u003e \u003cspan style=\"color:#00afaf\"\u003e\u0026#39;theme = \u0026#34;hyde-y\u0026#34;\u0026#39;\u003c/span\u003e \u0026gt;\u0026gt; config.toml\nhugo server\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eCongrats ㊗️\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/hugo-new-page.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eSuccessful\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003ch3 id=\"deploying-to-github-pages\"\u003eDeploying to Github Pages\u003c/h3\u003e\n\u003cp\u003eAs we know, Github Pages is a service which you can host your static files for free. I’ve created a new github \u003ca href=\"https://github.com/salimkayabasi/blog-with-hugo\"\u003erepository\u003c/a\u003e for hosting my new blog page.\u003c/p\u003e\n\u003cp\u003eDon’t forget to exclude \u003ccode\u003epublic\u003c/code\u003e folder from your github repo or you can check my \u003ca href=\"https://github.com/salimkayabasi/blog-with-hugo/blob/master/.gitignore\"\u003econfiguration\u003c/a\u003e for that.\u003c/p\u003e\n\u003cp\u003eSomehow we need to build those files with using Hugo and deploy them on github pages. Working with \u003ca href=\"https://travis-ci.org/\"\u003eTravisCI\u003c/a\u003e for your open-source project is also free.\u003c/p\u003e\n\u003cp\u003eWe need to create a file which named \u003ccode\u003e.travis.yml\u003c/code\u003e\u003c/p\u003e\n\u003cp\u003eIn Travis, we can choose our working platform so defining \u003ccode\u003elanguage: go\u003c/code\u003e would be helpful for working with Hugo.\u003c/p\u003e\n\u003cp\u003eThere are some flow to build your project but don’t forget that your own source code is not a Go project so we need to skip installing dependency part. Here, Hugo is using \u003ccode\u003egovendor\u003c/code\u003e to fetch its own dependencies.\u003c/p\u003e\n\u003cp\u003eBefore running our build scripts, we need to install Hugo and related dependencies with using govendor.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan style=\"color:#0087ff\"\u003ebefore_install\u003c/span\u003e:\n  - go get github.com/kardianos/govendor\n  - govendor get github.com/gohugoio/hugo\n  - go install github.com/gohugoio/hugo\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eNow, we are able to generate our html files with using Hugo.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan style=\"color:#0087ff\"\u003escript\u003c/span\u003e:\n  - hugo version\n  - rm -rf public || exit 0\n  - git clone https://github.com/$THEME\\_REPO.git themes/current-theme\n  - rm -rf themes/current-theme/.git\n  - hugo\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eHugo is using \u003ccode\u003epublic\u003c/code\u003e folder as output folder by default so it would be great to remove public folder if we have. On local development, we cloned into themes folder. It would be same for our travis configuration. Managing theme in separated github repository will help you to focus on your content. Hidden trick, use always same folder for your theme and no need to change your theme config on \u003ccode\u003econfig.toml\u003c/code\u003e file\u003c/p\u003e\n\u003cp\u003eHugo generated our html files and we are ready to publish them on Github Pages.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan style=\"color:#0087ff\"\u003edeploy\u003c/span\u003e:\n  \u003cspan style=\"color:#0087ff\"\u003eprovider\u003c/span\u003e: pages\n  \u003cspan style=\"color:#0087ff\"\u003eskip_cleanup\u003c/span\u003e: \u003cspan style=\"color:#d75f00\"\u003etrue\u003c/span\u003e\n  \u003cspan style=\"color:#0087ff\"\u003egithub_token\u003c/span\u003e: $GITHUB_TOKEN\n  \u003cspan style=\"color:#0087ff\"\u003elocal_dir\u003c/span\u003e: public\n  \u003cspan style=\"color:#0087ff\"\u003efqdn\u003c/span\u003e: $DEPLOY_DOMAIN\n  \u003cspan style=\"color:#0087ff\"\u003eon\u003c/span\u003e:\n    \u003cspan style=\"color:#0087ff\"\u003ebranch\u003c/span\u003e: master\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eOn Travis, they have a deployment tool for github pages. No need to clean the working folder before releasing because we will use \u003ccode\u003epublic\u003c/code\u003e folder. With the help of \u003ccode\u003efqdn\u003c/code\u003e configuration, we can use our custom domain for our blog page.\u003c/p\u003e\n\u003cp\u003eFinal version of Travis configuration is accessible \u003ca href=\"https://github.com/salimkayabasi/blog-with-hugo/blob/master/.travis.yml\"\u003eon github\u003c/a\u003e also.\u003c/p\u003e\n\u003ch3 id=\"last-steps\"\u003eLast steps\u003c/h3\u003e\n\u003cp\u003eOn Github, we need to enable github pages feature regarding to our configuration.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/github-pages.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eGithub Configuration\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eDon’t forget to pass your environment variables to Travis for successful build process.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eDEPLOY_DOMAIN will be your custom domain address\u003c/li\u003e\n\u003cli\u003eGITHUB_TOKEN is your private access token to deploy generated files to github\u003c/li\u003e\n\u003cli\u003eTHEME_REPO is github repo name for your favourite theme\u003c/li\u003e\n\u003c/ul\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/travis-envs.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eTravis Env Variables\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eHere is the my Cloudflare configuration for custom domain\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/cloudflare.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eCloudflare\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003ch3 id=\"first-page\"\u003eFirst Page\u003c/h3\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre style=\"color:#8a8a8a;background-color:#1c1c1c;-moz-tab-size:2;-o-tab-size:2;tab-size:2\"\u003e\u003ccode class=\"language-shell\" data-lang=\"shell\"\u003ehugo new posts/first-page.md\n\u003cspan style=\"color:#4e4e4e\"\u003e# my-blog/content/posts/first-page.md created\u003c/span\u003e\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eStill, you can work with Hugo CLI or just create your markdown file on given folder above.\u003c/p\u003e\n\u003ch3 id=\"conclusion\"\u003eConclusion\u003c/h3\u003e\n\u003cp\u003ePlease check my source code on my \u003ca href=\"https://github.com/salimkayabasi/blog-with-hugo\"\u003egithub repo\u003c/a\u003e to see more example about blogging with Hugo. Also, on Hugo’s documentation you can discover a lot of cool features for you.\u003c/p\u003e\n\u003cp\u003eKeep generating valuable content ✍️\u003c/p\u003e\n"},{id:8,href:"/blog/2016/10/talking-at-it-weekend-dnipro/",title:"Talking at IT Weekend Dnipro",description:null,content:"\u003cp\u003eNext week, I’ll be at Dnipro, Ukraine. Currently, I am preparing for my talk which is about \u003cem\u003eMobile Websites\u003c/em\u003e. With this talk, we are going to discuss history and future of web development.\u003c/p\u003e\n\u003cp\u003eThis event will be held on 22nd of October at Dnipro, Ukraine. Today, I was at the racecourse and wanted to record a video for you.\u003c/p\u003e\n\u003cp\u003eDon’t forget to check the agenda and register for the event.\u003c/p\u003e\n\u003cp\u003e\u003ca href=\"https://itweekend.ua/announcements/itw-dn-16o/\"\u003ehttps://itweekend.ua/announcements/itw-dn-16o/\u003c/a\u003e\u003c/p\u003e\n\n\u003cdiv style=\"position: relative; padding-bottom: 56.25%; height: 0; overflow: hidden;\"\u003e\n  \u003ciframe src=\"https://www.youtube.com/embed/WUTJ38z21Wk\" style=\"position: absolute; top: 0; left: 0; width: 100%; height: 100%; border:0;\" allowfullscreen title=\"YouTube Video\"\u003e\u003c/iframe\u003e\n\u003c/div\u003e\n\n\u003cp\u003eI hope that my friends who are living at Lviv won’t get angry with me because I missed the latest DevFest in Ukraine and now traveling to Dnipro.\u003c/p\u003e\n"},{id:9,href:"/blog/2016/10/will-project-fi-be-our-next-carrier/",title:"Will Project Fi be our next carrier",description:null,content:"\u003cp\u003eI’ve spent enough time with Project Fi and wanted to share my experience with it.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/fi-hello.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eProject Fi\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eGoogle announced the \u003ca href=\"https://fi.google.com/about/\"\u003eProject Fi\u003c/a\u003e as a new wireless communication service. It’s working all over the world but you have to apply with an US related \u003ccode\u003egmail\u003c/code\u003e account.\u003c/p\u003e\n\u003cp\u003eProject Fi is using Wi-Fi and cell networks for voice call and text. Most of the time it prefers to use Wi-Fi for better network coverage and reduce data that you used.\u003c/p\u003e\n\u003cp\u003eIf we mentioned about data usage, pricing is so simple and quite fair. You will pay 10$ per GB and it will be billed in MB according to data that you used.\u003c/p\u003e\n\u003cp\u003eFor example, you chose 3GB for monthly usage but used 2.28GB. That means you will not pay $7.2 for unused data.\u003c/p\u003e\n\u003cp\u003eAnyone can find more details about the project with using its website but I would like to mention about international coverage of Project Fi and my experience within last seven months.\u003c/p\u003e\n\u003cp\u003eFirst of all, I was using Google Voice for my international calls and will compare those all features between Project Fi in another post.\u003c/p\u003e\n\u003cp\u003eMy phone is Nexus 5X and it comes with LTE Cat6 (300/50 Mbps) support. I haven’t seen any speed like that :) But 100 Mbps is easy to reach in San Francisco, CA around the city center.\u003c/p\u003e\n\u003cp\u003eThat sounds good but what about other countries. I went to Turkey, Germany, Czech Republic and Greece, that was a good chance to check the quality of Fi network. At the beginning of the Project Fi, there is no 4G/LTE support for international usage. Just couple weeks before my trips, Fi team announced that they will cover with 4G network in most countries.\u003c/p\u003e\n\u003cp\u003eSo time to check the quality in Istanbul, Turkey. At first attempt, whooho, there is no cellular network. No way to call anyone or connect the internet. I tried to fix that problem and couldn’t succeed. After talking with Fi Support, they told me that my phone was trying to connect 4G network but there was no good network quality and operators in Turkey were not working with Project Fi very well. The solution was forcing network type to 3G.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/network-switch.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eForcing Nexus 5X for 3G network\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003eI tried everything that they told me and got the signal couple of hours later. With using 3G, that was not a good experience which is about 7–10 Mbps and need to try 4G in Turkey. If you give enough time for connectivity, you will get 4G signal which is about 80 Mbps.\u003c/p\u003e\n\u003cp\u003eGreece was the next destination of my trip. There is no strange story here. According to my speed tests, Fi network is quite good in Thessaloniki, Chalkidiki and Thassos Island. Most of the time I reached about 100–120 Mbps during my trip. It was easy to connect Fi Network which took only seconds.\u003c/p\u003e\n\u003cp\u003eA couple of weeks ago, I went to Berlin, Germany. Same here, I could connect to Fi network without pain. The speed of internet connection was about 100–120 Mbps again. But a few days later, the problem came out about my connectivity. I could connect to the internet but can not call anyone if connected the only cellular network.\u003c/p\u003e\n\u003cp\u003eIssued my case but there was no solution. Hopefully, \u003ca href=\"https://play.google.com/store/apps/details?id=com.google.android.apps.hangoutsdialer\u0026amp;hl=en\"\u003eHangouts Dialer\u003c/a\u003e was working over cellular network. Almost a week passed after beginning of my case, we were still looking for a solution. Project Fi refunded 30$ because of one week with this case.\u003c/p\u003e\n\u003cp\u003eNow we were way on to Prague, Czech Republic. Project Fi team called to me and gave some instructions. That helped me connect to cellular network then got succeed for calling with using the cellular network. I called my friend, yessss that was amazing. But when I wanted to call another friend, faced with the same problem. There was no an exact solution for my case.\u003c/p\u003e\n\u003cp\u003eThe last destination was Turkey, my phone is still getting problems about calling over the cellular network. According to Fi team, this is an international only case and will fix ASAP and not related my sim card or phone.\u003c/p\u003e\n\u003cp\u003eIf we summarize it, Project Fi is a kind of public beta but mature enough to use if you are an international traveler. No need to carry lots of sim cards for each country. Pausing service anytime whenever you want and resume again.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eupdate:\u003c/strong\u003e I’ll be in Ukraine next week and make some additional comment about Project FI.\u003c/p\u003e\n"},{id:10,href:"/blog/2015/07/yandex-m%C3%BC%C5%9Fteri-ili%C5%9Fkileri/",title:"Yandex müşteri ilişkileri",description:null,content:"\u003cp\u003eBugüne kadar bir çok firmayla ciddi kavgalar ettiğimi ve bir çoğunda da haklı olan tarafta olduğum için direne direne kazandığımı blogumdaki diğer yazılardan anlayabilirsiniz. Saatler süren telefon kavgalarımdan rahatsız olduğu için ev arkadaşım da bazen kapısını falan kapatır.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eGelelim Yandex ile yaşadığım senaryoya;\u003c/strong\u003e\u003c/p\u003e\n\u003cp\u003eGiriş kısmına bakarak, Yandex ile nasıl kavga ettin diye düşünmeyin gerçekten çok ciddi bir memnuniyet yazısıdır :)\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e25 Aralık 2014 Perşembe:\u003c/strong\u003e\u003c/p\u003e\n\u003cp\u003eYaşadığım bir sıkıntı üzerine ofisteki arkadaşlarla da karşılaştırmalı testlerimize göre ortada bir sorun vardı. Yandex Maps lokasyonumu doğru gösteremiyordu. Geliştirdiğimiz uygulama lokasyon bazlı olduğu için sık sık cihazdan da testler yapıyordum. Çok saşırtıcı bir sonuç verince bunu Youtube üzerinden paylaşmak istedim.\u003c/p\u003e\n\n\u003cdiv style=\"position: relative; padding-bottom: 56.25%; height: 0; overflow: hidden;\"\u003e\n  \u003ciframe src=\"https://www.youtube.com/embed/GSOTw824pUY\" style=\"position: absolute; top: 0; left: 0; width: 100%; height: 100%; border:0;\" allowfullscreen title=\"YouTube Video\"\u003e\u003c/iframe\u003e\n\u003c/div\u003e\n\n\u003cp\u003eÖzetle Yandex’ın lokasyon servisi garip bir şekilde Uzunçayır metrobüs durağına bizi sabitliyordu. Yandex servisi kullanan BiTaksi’nin de aynı şekilde lokasyonu sabitliyor olması ciddi sorun çıkarıyordu. Taksi çağırmak istesem ve dikkatsiz olsaydım farklı bir adrese taksi gitmesi gerekecekti. \u003cstrong\u003e8 Ocak 2014 Perşembe:\u003c/strong\u003e\u003c/p\u003e\n\u003cp\u003eAradan geçen 2 haftadan sonra beklenmedik bir şekilde Yandex takımından mail aldım. Bu belirtilen 2 haftanın içerisinde 4 günlük kar tatili ve yılbaşı tatili olduğunu da düşünürsek gayet hızlı bir şekilde dönüş yapıldığı için şaşırdım. Daha ilginç olanı videonun sadece 48 defa izlenmiş olmasına rağmen Yandex’in ürüne sahiplenerek müşterisine sorunun devam edip etmediğini sorması oldu.\u003c/p\u003e\n\u003cp\u003eSorunun devam ettiğini ve hangi koşullarda karşılaştığımı ve yaptığım testleri içeren bir mail attığımda karşımda konusunda yeterli teknik donanıma sahip birisi olduğunu görünce çok saşırdım. Çünkü normalde bu tarz sosyal medya üzerinden müşteri ilişkileri yönetmek isteyen firmalarda sadece kayıt açan ve hazır cevap veren kişiler bulunuyor. Yandex’i de bu konudaki özenli çabasından dolayı tebrik etmek gerekiyor.\u003c/p\u003e\n\u003cp\u003eSorunumu özetlemek gerekirse \u003ca href=\"http://en.wikipedia.org/wiki/Wi-Fi_positioning_system\"\u003eWi-Fi positioning system\u003c/a\u003e olarak bilinen ve GPS olmadan da lokasyon bulmayı sağlayan bu sistemin güncelleme hızı ile ilgili bir sıkıntı oluşmuş. Bunun da sebebi aslında çok yakın tarihte bizim ofisimizi taşıyor olmamızdır.\u003c/p\u003e\n\u003cp\u003eYandex personelinin konuyu açıklarken yeterince detaya girmesi ve üstüne benzer bir sorunu kendi ofislerini taşırken de yaşadıklarını çok uzun sürmeyeceğini belirttiler. Yaşadığım sorunun inanılmaz bir sıkıntı yaratmamasına rağmen bu kadar ilgili olmaları, rakip firmalar ile ilgili cümleleri kullanırken gösterdikleri özen ve bence en önemlisi kendi dilimden birisi ile bu problemin çözümü için iletişimde oluyor olmak beni gerçekten mutlu etmiştir.\u003c/p\u003e\n\u003cp\u003eSorduğum sorulara da 18:20 olmasına rağmen cevap veriyor olmalarını da unutmamak gerekiyor\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003ePS:\u003c/strong\u003e Mail içeriğindeki teknik detayları sizleri sıkmamak adına çıkardım ve özetlemeye çalıştım, teknik tarafını merak eden varsa sorabilir :) Yandex ekibindeki çalışanlara isimlerini de belirterek teşekkür etmek istedim ama isim paylaşılmaması üzerine anlaştığımız için belirtemiyorum\u003c/p\u003e\n"},{id:11,href:"/blog/2015/07/huzurumu-%C3%A7ald%C4%B1lar/",title:"Huzurumu çaldılar",description:null,content:"\u003ch3 id=\"10-eylül-2014\"\u003e10 Eylül 2014\u003c/h3\u003e\n\u003cp\u003e\u003cstrong\u003e15.45—\u003c/strong\u003e Ofiste normal bir gün çalışıyoruz, ev sahibinin oğlu aradı. Sizin eve girenler olmuş, sanırım hırsız girmiş bir gelsen iyi olur dedi. Geliyorum diyerek kapattım. Kahrolası İstanbul trafiğinde tabiki de Maslak’tan Mecidiyeköy’e gelmek çok da kolay olmadı.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e16.02—\u003c/strong\u003e Emin olmadan polisi aramak istemedim ama yolda giderken tekrar gelen telefonla, artık emin olunca 155\u0026rsquo;i arayarak durumu izah ettim.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e16.13—\u003c/strong\u003e Hala trafikteyken, 0 5XX 3XX 7XX9 numaralı birisi aradı, polis olduğunu şöyleyen kişi, hırsızlıktan emin olup olmadığımı, evde çalınan eşya olup olmadığını, evime ulaşmak üzere yolda olduklarını söyledi.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e16.35—\u003c/strong\u003e Evin önüne geldiğimde, kapı kilidinin göbek denen kısmının (anahtarın takıldığı kısmı) kırık olduğunu farkettim. Çok az da olsa kilit civarında da hasarlar var.\u003c/p\u003e\n\u003cp\u003eAnlatılanlara göre yaklaşık 3 ay önce 2 üst komşunun evine de gündüz vakti girmişler ve evde 10 yaşlarındaki küçük çocuk varmış. Annesinin bana anlattığına göre çocuk yine evde tek başınayken annesini arayıp dışarıdan yemek için bir şeyler almak için izin istemiş. Saat \u003cstrong\u003e15.00\u003c/strong\u003e gibi çocuk aşağı inip alış veriş yapmış. İnerken ya da tekrar eve çıkarken, hırsızlarla karşılaşıyor. Uzun boylu, esmer, kıvırcık saçlı, zayıf, mavi ve beyaz kıyafetleri olan birisini evimin önündeki merdivende görmüş. İçeriye de Aydın ya da Ayhan diye seslenerek \u0026ldquo;Gel artık” demiş. Çocuğa göre kendi evlerine giren şahıs ile aynı kişi. Çocuk eve tekrar döndüğünde saat \u003cstrong\u003e15.30\u003c/strong\u003e civarı korkarak annesini aramış. Komşular aracılığı ile olayın bana aktarılması \u003cstrong\u003e15.45\u003c/strong\u003e\u0026lsquo;i buluyor.\u003c/p\u003e\n\u003cp\u003eBeklerken kapıyı açmak için tornavida gibi bir takım aletlerle denemeler oldu. 2 tane polis geldi bu sırada. Kapıyı açamadık çilingir çağırdık. Beklerken ev arkadaşım da gelmesiyle 7–8 birikmişti.\u003c/p\u003e\n\u003cp\u003eHırsız evi boşaltırken hiç kimsenin ortalıkta olmadığı duymadığı apartmanda bu ne kalabalık ve inanılmaz bir gereksiz gürültü diye giderek sinirlendim.\u003c/p\u003e\n\u003cp\u003eElimdeki anahtarın parçası ile kapının kilidini bir şekilde çevirerek, kapıyı açtım. Tabi ki de içeriye işi olan olmayan herkes girdi. Hızlıca odalara bakındım, ortalık ciddi oranda dağıtılmış ilk etapta dikkat çeken ve çalınma ihtimali olan eşyalarıma baktım. Polis çalınan bir eşyanız var mı dedi.\u003c/p\u003e\n\u003cp\u003eGelen ekipteki polis herhangi bir işlem yapmadan karakola gitmemiz gerektiğini söyledi. Ev arkadaşımı alarak karakola gittiler, ki o gelmeden önce ev soyulmuş mu emin misiniz diyerek teyit alıp trafikteyiz dedikleri karakol yürüyerek 4 dakika uzaklıktaymış.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e16.50—\u003c/strong\u003e Ev arkadaşım çalışan eşyaların neler olduğunu sordu, karakoldan telefon ederek. Yanındaki polisle konuşup bunların şimdi değil sonra halledileceğini, şimdi sadece \u0026ldquo;Olay Yeri İnceleme” ekiplerini çağırabilmek için evrak doldurulması gerektiğini söylemiş. Gelen polislerin evde bekleme süresi kapıda bekleme süresinden az, Başka bir polisi daha beklemek için karakola kadar gidip evrak doldurup geri gelmek gerekiyormuş.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e17.04—\u003c/strong\u003e Çilingir geldi, adam o kadar baskı yaptı ki kilidi hemen değiştirip gitmek için. Sırf inansın diye ev arkadaşımı aradım kilidi değiştirme konusunda polis ne diyor gibisinden sordum. \u0026ldquo;Olay Yeri İnceleme” ekibinin yolda olduğunu birazdan geleceklerini dokunmamalarını söyledi diyerek adamı biraz oyaladım.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e17.22—\u003c/strong\u003e Adamı tutmanın manası yok hemen geliyorum diyen polisleirn kaçta geleceği belli olmaz diyerek sonra haber veririz dedim ve adamı gönderdim.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e17.30—\u003c/strong\u003e Zile basıp içeri gelen iki kişi \u0026ldquo;Olay Yeri” ekibi olduğunu söyledi, bizden kimsenin de aklına kimlik sormak gelmedi. Ne resmi bir kıyafet ne bir iş ciddiyeti yoktu üstlerinde.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e18.01—\u003c/strong\u003e Tekrar çilingiri aradım. Tam da çilingirin söylediği gibi kapıya bakmadılar bile. Adam geldi her zaman böyle oluyor abi dedi kilidi değiştirdi. Bu gece idare edecek kadar dandik bir kilit tak umrumda değil dedim ama 100TL’cik borcumuz oldu.\u003c/p\u003e\n\u003cp\u003eİçeride odaları dolaşırken ekipten şu lafı duyduğumda çok sinirlendim. \u0026ldquo;Monitörler güzelmiş, onları neden götürmemişler.” Kafamdaki tek soru, komisyon mu alıyorsun diye ama sorup ortamı germek istemedim. Adam odaları dolaşırken, salondaki basküllere çıkıp ağırlığını kontrol edecek kadar kendini de rahat hissediyor ayrıca.\u003c/p\u003e\n\u003cp\u003eSonradan edindiğim olay yeri inceleme raporu formundaki bilgiler de tam manasıyla komedi, söylenene göre\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e17.00\u0026rsquo;da bildirilmiş, tahmini olarak ev arkadaşım karakoldayken arayıp bildirmişler\u003c/li\u003e\n\u003cli\u003e17.10 olay yerine intihal yazıyor, benim telefondaki arama kaydıma göre 17.22\u0026rsquo;de ben çilingiri gecikecekler sanırım diyerek gönderdiğimi biliyorum.\u003c/li\u003e\n\u003cli\u003e11.15 incelemeye başlama yazıyor. Tamamen özensiz iş yapan devlet memuru önceki raporu düzenlerken ciddiye almadan yazmış geçmiş. Tutarsız bir saat kısaca\u003c/li\u003e\n\u003cli\u003e18.15 inceleme bitiş saati yazıyor ama 18.01\u0026rsquo;de iş bitti sanırım çilingiri arayabilir miyim dediğim için bu saatin de kafasına göre yazıldığı ortada\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003ePolis raporuna göre evime 10 dakika içinde gelip 7(!) saat inceleme yapılmış. Sanırım 1 saat demek istedi orada ama olmadı. Benim takibime göre, sadece yarım saat kaldılar evin içinde.\u003c/p\u003e\n\u003cp\u003eBuraya kadar işlenmesi gereken prosedürün ne olduğunu ve olacağını kimse net olarak bize bildirmiyordu. Ne zaman ifademiz alınacak, idari süreç nasıl ilerliyor kimse bir yönlendirme yapmıyordu. Ama bu raporda \u0026ldquo;Mağdur / Şikayetçinin Daveti” yapıldı diye işaretlenmiş.\u003c/p\u003e\n\u003cp\u003eTabiki de hiç bir parmak izi bulunamadı, benim bile parmak izim yokmuş hiç birinde…\u003c/p\u003e\n\u003cp\u003eEvden çıkarken bu polislerin verdiği tek yönlendirme şu şekildeydi. İnternet sağlayıcınız ile görüşüp, bu hizmeti kullanan tüm MAC adreslerini tespit edilip, buna yönelik bu cihazların başka bir internet ağına bağlandığı tespit edilirse buna göre takip ediliyor dedi.\u003c/p\u003e\n\u003cp\u003eBöyle bir işlem olsa dahi, en fazla modeme ait MAC adresi bulabilir dedim. Ayrıca müşteri hizmetlerini arayıp böyle bir işlem talep etsem dahi sanmıyorum dedim ve çıktılar.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e18.17—\u003c/strong\u003e Sırf denememiş olmamak için internet hizmeti aldığım Turksat Kablo Hizmetleri’ne ait müşteri hizmetleri numarasını aradım, tabiki de beklenen durum 6 dakika 29 saniyelik görüşme sonucunda modemin MAC adresini verebiliriz. Bahsedilen şekilde bir MAC adresi takip etmek gerekiyorsa bunun yasal kanallardan, yazı gelmesi gerekiyor bu şekilde olmaz dedi. Sadece güldüm beklenen bir durumdur.\u003c/p\u003e\n\u003cp\u003ePolisin internet sağlayıcısına, onların da polise attığı saçma bir durumdur.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e19.10—\u003c/strong\u003e Bundan sonra neler yapılacağını öğrenmek için önte 155\u0026rsquo;i arayarak ilgili karakolun numarasını talep ettim. Karakolu arayıp sürecin takibini nasıl yapmamız gerekiyor diye sordum.\u003c/p\u003e\n\u003cp\u003eİfade vermeniz gerekiyor ama şu an burası biraz kalabalık 4 5 olay daha var bence yarın gelin dedi.\u003c/p\u003e\n\u003cp\u003eArayıp sormasak kimse bizi yönlendirmiyor o olaysa bu da olay diyerek çıktım dışarı, karakola gitmeden önce dışarda karnmı doyurdum.\u003c/p\u003e\n\u003cp\u003eDışarıya da çıkmışken etraftaki esnafa da olayı anlatarak kamera kaydı olup olmadığnı sordum. Tabiki de polis hiç birine sormamış hepsi yardımcı olmak için elinden geleni yaptı.\u003c/p\u003e\n\u003cp\u003eOlayı anlatıp ifade vermek istediğimi söyledim. Girişte \u0026ldquo;Danışma” yazan yerdeki adının sonradan \u0026ldquo;Mustafa” olduğunu ögrendiğim sicil numarasının 27 ile başladığını hatırladığım polis memuru ile uzun uzadıysa yer yer ses yükselterek(!) konuştuk.\u003c/p\u003e\n\u003cp\u003eNeden kimse yönlendirmiyor sorusuna, yeni polis arkadaşların bir hatası olmuş. Bazen unutuyorlar ama ben onların yerine özür dilemem, olay yericiler de mi söylemedi bir şey diyerek aslında suçu başkasına atmak istedi. Özetle bunu geçiştirdi.\u003c/p\u003e\n\u003cp\u003eNeden ifadem alınmıyor konusunda ise, bak bu adam da akşam 7\u0026rsquo;den beri bekliyorlar. Onlar sorgulamuyor sen neden sorguluyorsun diyerek yine alakasız bir cevap verdi. Bu adam da buraya gelmeden 2 karakol daha gezmiş. Adamın işi bitmemiş o senin kadar sinirli değil dedi. Belki de sorgulamadığı için işi bu kadar uzun sürüyor peşine düşmesi lazım dedim. Buna cevap veremedi tabiki de.\u003c/p\u003e\n\u003cp\u003ePOLNET sisteminin arızalı olduğunu hiç bir işlem yapılamadığını söyledi. Bu durumun çok sık olduğunu hatta bazen havaalanlarında insanların mağdur olduğunu, uçakların kalkmadığını falan anlatıyor. Bu anlatılanların hiç biri olan durumun açıklaması değil tabiki de.\u003c/p\u003e\n\u003cp\u003eBiz de adliyeye gittiğimizde UYAP arızalı diyorlar saatlerce bekliyoruz, biz sana bekleme eve git yarın gel diyoruz gibisinde de yönlendirmeler devam ediyor.\u003c/p\u003e\n\u003cp\u003eKonuşma sırasında gelen telefonlara da bakıyor ara ara, Dereboyu caddesi için bir şikayet geldiğini işittim. Saate göre gürültü konusunda bir şikayet değildir diye düşündüm içimden. Yaklaşık 10 dakika sonra dışarıda muhabbet eden polislerden birisine seslenerek, durumu izah ettiğinde öğrendim ki silah sesi gelmiş, 45 dakika önce 155\u0026rsquo;i aramış vatandaş ama kimse ilgilenmemiş. Ben de gözümle görüyorum ki, bizim mahalleden de sorumlu olan Kuştepe Polis Merkez Amirliği işi çok ciddiye almıyordu. Kapıda seslendiği polis elindeki tesbihi sallayarak geldi ve \u0026ldquo;abi elimde bir hırsızlık var bitsin giderim” dedi. O da işle ilgilenmek istemedi ve haliyle konu kapandı.\u003c/p\u003e\n\u003cp\u003eIsrarla neden hiç bir kamera görüntüsünün esnaftan ya da MOBESE’den talep edilmediğini sorduğumda asrın beklenen cevabını verdi. Biliyorsunuz bir çoğu çaılışmıyor, sen orada onun kodunu ver biz ona bir yazı yazalım duruma göre baksınlar dedi. 91 ile başlayan bir kodu var onu bize bildir dedi.\u003c/p\u003e\n\u003cp\u003eÇalınan eşyalar arasında pasaportun da olduğunu neler yapılması gerektiğini sorduğumda yarın buradan sana bir evrak vereceğiz, sen de onu alıp pasaport şubeden halledeceksin dediler. O zaman alın raporu diye ısrar etsem de yapamayız, hem saati belli başına bir bela gelmez sorun yapma dediler.\u003c/p\u003e\n\u003cp\u003eBiraz daha tartıştıktan sonra, neyse yarın gelirim diyerek çıktım.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e21.43\u003c/strong\u003e —Evin önündeki en işlek caddeye ama sadece kırmızı ışıkları takip etmesi için koyulmuş olan MOBESE kamerası bizim sokağı görüyor mu bilmiyorum ama bahsedilen kodu öğrenmek için inceledikten sonra üstünde 91 ile başlayan bir kod olmadığını gördüm ve karakolu tekrar aradım.\u003c/p\u003e\n\u003cp\u003eBelki de aynı polisle tekrar konuşup kodun \u0026ldquo;ŞİŞLİ 022” olduğunu, başka bir kod olmadığını emin misiniz 91 ile başladığını yoksa ben mi hata yapıyorum dedim. Bunu ben buraya yazarsam kaybolur sen yarın gelince ifadene incelenmesini istediğini eklet diyerek kapattı konuyu. Yani mahallede hırsızlık olunca MOBESE kaydını vatandaşın ifadeye ekletmesi gerekiyormuş.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e22.32\u003c/strong\u003e —Kapım çaldı. Karşımdaki komşu heyecanlı bir şekilde, sizin eve hırsız mı girdi dedi. Kendi derdimizden hiç dikkat etmedik ama aynı şekilde o evin de kapısını kırmışlar. Hemen çilingiri çağırdık çünkü yine kapı açılmıyordu.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e23.12—\u003c/strong\u003e Polisi gereksiz yere meşgul etmemek adına eve hırsız girip girmediğinden emin olmak istedik çilingiri bekliyorduk. Yeterince bekledik ama gelen giden olmadığı için ben 155\u0026rsquo;i aradım. İnsanların polisi arama amacı nedir bilemem ama nasıl meşgul çaldığını çok merak ettim. Hemen arkasından şikayet için gittiğim karakolu arayıp olaydan bahsettim.\u003c/p\u003e\n\u003cp\u003eBana mağdur olan kişinin direk karakola gelip, olay yeri için istek formu doldurması gerektiğini söyledi. Evde tek kalan birisi için ve ayrıca sadece gidip bir form doldurup olay yerini çağırmak için neden gidip evrak doldurmak gerekiyor anlamış değiliz.\u003c/p\u003e\n\u003cp\u003eİkinci arama sonrasında, olay yeri inceleme ekibini yönlendirdiklerini söylediler. Yani bizi sabah boşuna götürmüşler karakola kadar evime gelen polisler bunu aslında halledebilirlermiş.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e23.36\u003c/strong\u003e—Süreci takip etmek için işleri hızlandırmak için tekrar aradım. Tam da bu sırada olay yeri inceleme ekibi geldi. Ekibin gelme süresi 24 dakika.\u003c/p\u003e\n\u003cp\u003eOlay yeri incelemeye devam ettiği sıra, umarım buradan parmak izi çıkar çünkü %99 bizim eve girenle aynı kişiler dedim. Polis düzeltti, direk aynı kişiler ihtimal yoktur. Aynı gün iki hırsız gelmez dedi. O evden 3 parmak izi alındı. Sonuçların ne olacağı tabiki sonra ortayaca çıkacaktır.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e11 Eylül 2014\u003c/strong\u003e\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e10.00—\u003c/strong\u003e Oturdum ikinci el satış yapan sitelerden, evimden çalınan cihazlar olabilir mi acaba diye bakındım. Belki de profesyonel birisi değildir diye düşündüm,\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e11.00—\u003c/strong\u003e Karşı komşu kapıyı çaldı, esnaftaki kameralara bakmak için beraber gidelim dedi.\u003c/p\u003e\n\u003cp\u003eHazırlanıp aşağı indiğimde komşum, koşe başındaki teknik servise kamera için gittiğini söyledi. Kamera yokmuş ama farklı haber var, dün hemen üst kata da hırsız girmiş dedi.\u003c/p\u003e\n\u003cp\u003eTam o sırada, bizim sokakta yine polisleri gördük. Meraklı komşu edasıyla sorduğumuzda yine bir evden hırsızlık dediler. Belki de aynı adam girdi o eve de ama yeni farkediliyor. Aynı sokakta 2\u0026rsquo;si bizim apartmanda olmak üzere 4 eve, 2 dükkâna, 6 Nokta Görme Engelliler Derneği’ne girip işlerine yarayabilecek ne varsa toplayıp çıkmışlar.\u003c/p\u003e\n\u003cp\u003eBir kaç esnafı ziyaret ederek elde edilebilecek kamera görüntülerini topladık ve karakola gittim.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e12.45\u003c/strong\u003e —Dünkü gittiğim karakola tekrar gittim. Durumu anlattım, ifade için geldik dedim. Biraz bekleyin ekipler başka bir hırsızlık için gittiler dedi. İfade almakla/yazmakla görevli olan polis hırsızlık için mi gitmiş acaba diye düşündüm.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e13.00—\u003c/strong\u003e Biraz bekledikten sonra olayın saçma olduğunu neyi beklediğimizi öğrenmek için tekrar gidip ne kadar beklememiz gerekiyor acaba diye tekrar sordum. Bu sırada polislerin bir çoğunda resmi kıyafetin zorunluluğu olan sicil numaralarının olmadığını farkettim. Şu an içeride ifade alınıyor bitsin sizi alırız dediler.\u003c/p\u003e\n\u003cp\u003eİlerleyen süreçte dışarıda bekledikçe bizi unuturlar diye düşünüp karakolun bahçesinden içeriye girdim. Bu sırada başka sebeplerden gelen diğer vatandaşlara da farklı izahlarla orada bekletildiğini gördüm.\u003c/p\u003e\n\u003cp\u003eİçeriden gelen bir polis sizin olay \u0026ldquo;zenci” olayı mı dedi. İçeriden duyduğum kadarıyla, bir vatandaşın kiracasıyla ilgili sıkıntı olmuş arap asıllı olan kiracılara polisin \u0026ldquo;zenci” demesi garip geldi.\u003c/p\u003e\n\u003cp\u003eSöylemek istediği şeyi anlayıp peşinden gittim, çünkü bahsi geçen olay ile ilgili insanlar bizden sonra gelmişti. Benim ifadem alınmadan onların içeri alındığını gördüm. Artık dayanamayıp, sert bir şekilde dalga mı geçiyorsunuz, ben geleli 1 saat oldu daha neyi bekliorum dedim.\u003c/p\u003e\n\u003cp\u003eSırayla ifade alınıyor, beklemeniz gerekiyor dedi. Sıraysa ben geldiğimde kimse yoktu ne sırası dedim. İçeridekiler benden sonra geldi dedim. İçeri gitti geldi, onların ifadesi alınmıyor bilgilerine danışılıyor dedi. Çok farklı bir durum gerçekten. Çok sürmez dediler.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e13.30—\u003c/strong\u003e İfade için odaya girdik sonunda, ev arkadaşımın ifadesini alırken bana boş bir kağıt verip buraya çalışan eşyaların seri numaralarını yazar mısın dedi.\u003c/p\u003e\n\u003cp\u003eİçeride iki polis vardı bir tanesi sivil olarak içeride çalışıyor diğerine göre daha tecrübeli gibi bir tavrı vardı. Sivil olan polis bizimle ilgilenmiyor, başka işleriyle ilgileniyordu. Bizim ifademizi alan memur ara sıra soru sorduğunda cevap veriyordu sadece.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e13.44—\u003c/strong\u003e Öncesinde ben olayı hızlıca bir kere anlattım. Hırsızlardan, eve gelen polisler ve tavırlarından ayrıca bu karakoldaki polislerden de şikayetçi olduğumu söyledim.\u003c/p\u003e\n\u003cp\u003eHırsızlık ile ilgili ifadeyi alalım sonra onlara da bakarız dedi.Okuması için ifadeyi çıkarıp ev arkadaşıma verdi. Ben de okudum. harf hataları olan ifadeyi düzeltmesini istedim. Onlar çok önemli değil anlaşılır hatalar dedi. Yukarıda bahsettiğim tutanak saatlerindeki ciddiyet burada da vardı.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e14.05—\u003c/strong\u003e İfade sırası bana gelmişti, Tüm olayları tek tek anlatıp, herkesten şikayetçiyim dedim. İfadeyi alan memur, diğerine dönerek durumu sordu. Bunları ifademe yazın buradan çıkıp savcılığa gideceğim dedim. Tamam orada şikayet edersin biz burada hırsızlık ile ilgili kısmını alalım dedi.\u003c/p\u003e\n\u003cp\u003eİfade odasının kapısında A4 kağıt ile \u0026ldquo;IFADE ALINIRKEN GIRILMEZ” yazmasına rağmen, sık sık gelip bir şeyler soran polisler oldu. Bir yerden sonra sivil kıyafetli polis soru soranlara ifade alınıyor görmüyor musun bir de neden hep bana soruyorsunuz diyerek gelen polisi azarladı.\u003c/p\u003e\n\u003cp\u003eAma ifadenin sonuna evinin önünde terkedilmiş bir minibüs olduğunu anlatan yaşlı bir amca kapıdan içeri girdi. Bu da demek oluyor ki \u0026ldquo;Danışma” denen yerde hiç kimse yoktu ki amca kimi gördüyse ona ulaşmak için girdi. Ona da gereksiz savunmalar yaparak çıktı.\u003c/p\u003e\n\u003cp\u003eBenim ifademin alınması sırasında, sivil kıyafetli polis bir ara takip ettiği konu ile ilgili dosya numarasını hatalı olduğunu farkedip savcıyı aradı. Bu sırada benim ifade alınma işlemim devam ediyordu. Ben derdimi anlatırken, neden kamera görüntülerine bakılmadı, neden kimse yardımcı olmuyor, kimse doğru yönlendirmiyor diyerek serzenişte bulunurken sesimi yükseltmiştim.\u003c/p\u003e\n\u003cp\u003eSavcı ile görüşen polis beni azarlayarak telefonla konuşuyorum görmüyor musun dedi. Sabahtan beri ifade alınması dışında bir çok işle uğraşıp millete laf yetiştiren ama ara ara bize cevap veren kendi kendine ortamı geren bu memurun ifade sırasında bizi taciz etmesi de cabası.\u003c/p\u003e\n\u003cp\u003eİfade tutanağında işini aşırı ciddi yapan memurun okumam için verdiği tutanakta, bir sürü harf hatası, düşük cümleler ve önceki ifadeden kalan cümleler vardı. Lisede yazılı okuyan öğretmen edasında kalemle hatalı yerleri çizdim ve düzeltmesi için geri verdim.\u003c/p\u003e\n\u003cp\u003eİfade içinde çalınan eşyalar dışında hiç bir açıklayıcı yazı yoktu. Olayı anlatan, esnaftan duyduklarım, karakolla yaşanan sıkıntılar, polislerin yanlış yönlendirmesi gibi konulara girmemiş. Söylesem de ciddiye almadı.\u003c/p\u003e\n\u003cp\u003ePasaport konusuna gelince tekrar hatırlatarak tek bir satır eklediler. Pasaport için yapılacak işlemi tekrar sordum. Yapılacak hiç bir işlem yok bunu al pasaport şubeye git dediler. Yani bu kadar tatava boşuna yapılmış.\u003c/p\u003e\n\u003cp\u003ePolisin yönlendirmek adına evden hırsızlık olaylarında izlenen prosedür, kontrol edilmesi gereken durumlar gibi bir kaç soru soru sormasını beklerdim. Pasaportu ben söylemesem hiç biri sormayacaktı, evinizden kimlik pasaport gibi bir resmi evrak çalındı mı gibisinden.\u003c/p\u003e\n\u003cp\u003eEvin sigortası var mı, evde alarm sistemi var mı gibi belki de takip edilmesi gereken başka konular var bilmiyorum. IMEI takibi, MAC adresi takibi falan hiç de ciddi yapılıyormuş gibi değil.\u003c/p\u003e\n\u003cp\u003eBana söylenen, sadece bu telefona bir sim kart takılırsa, bir SMS gönderilir. O da sizi bulur. Yani yine arada kalan bir durum, bulması ne demek lokasyon takibi falan yok mu yani bilmiyoruz.\u003c/p\u003e\n\u003cp\u003eAn itibariyle sorguladığımda çalındığına dair bir ibare görünmüyor, ara sıra takip edip durumu değişecek mi diye bakıyor olacağım.\u003c/p\u003e\n\u003cfigure class=\"figure w-100\"\u003e\n  \u003cimg src=\"./images/imei-check.png\" class=\"w-100 figure-img img-fluid rounded\" \u003e\n   \u003cfigcaption class=\"figure-caption text-center\"\u003eIMEI Sorgulama\u003c/figcaption\u003e \n\u003c/figure\u003e\n\n\u003cp\u003e\u003cstrong\u003e15.30—\u003c/strong\u003e Evrakları alıp, Şişli Emniyet Müdürlüğü’ndeki pasaport şubeye gittim. İçeri girdiğimde yaklaşık 30 kişi bekliyordu. Kimsenin hayırdır neden geldin diyecek bir hali yok. Bir tane memur var oturuyor kimsenin de işi yapılmıyr gibiydi. Bir bayan, sistem gelmedi mi hala diye sorunca, aynı hikaye burada da var dedim.\u003c/p\u003e\n\u003cp\u003eGidip memura durumu anlattım, dilekçe yazmanız yeterli dedi. Aslında iki gündür yaşanan olaylar tamamen gereksizlikti. İki satır dilekçe yazıp, tutanağın da fotokopisini ekledim ki o kadar tutanak bir işe yarasın.\u003c/p\u003e\n\u003cp\u003ePolis en fazla pasaportun fotoğrafını değiştirip, birisine satarlar ama çipli olduğu için pek manası yok atarlar bir kenara dedi.\u003c/p\u003e\n\u003cp\u003eBu arada yeni pasaport istersen, 75TL defter masrafını ödersen bitiş süresi aynı tarih olacak şekilde yeni pasaportunu alabilirsin dediler. Bunun için herhangi bir zaman gelsem olur mu dedim, istediğin zaman gel dediler.\u003c/p\u003e\n\u003ch3 id=\"evimden-çalınanlar\"\u003eEvimden Çalınanlar\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003e\u003cstrong\u003eAsus Nexus 7(2012)\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eAsus Nexus 7(2013)\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eAcer Iconia Tab 8 A1–840FHD\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eSony Playstation 4\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003ePlaystation Vita\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eSamsung Galaxy Nexus\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eSamsung Galaxy Gear\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eHP Chromebook 11\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eFujiFilm S1500\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eXBOX kumandaları\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003ePasaport\u003c/strong\u003e\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eHuzurum\u003c/strong\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eOlayın devamında da kapı, kilit, sigorta, alarm gibi konularla ilgili tecrübelerimi yazmayı planlıyorum.\u003c/p\u003e\n"},];index.add(docs);userinput.addEventListener('input',show_results,true);suggestions.addEventListener('click',accept_suggestion,true);function show_results(){var value=this.value;var results=index.search(value,5);var entry,childs=suggestions.childNodes;var i=0,len=results.length;suggestions.classList.remove('d-none');results.forEach(function(page){entry=document.createElement('div');entry.innerHTML='<a href><span></span><span></span></a>';a=entry.querySelector('a'),t=entry.querySelector('span:first-child'),d=entry.querySelector('span:nth-child(2)');a.href=page.href;t.textContent=page.title;d.textContent=page.description;suggestions.appendChild(entry);});while(childs.length>len){suggestions.removeChild(childs[i])}}
function accept_suggestion(){while(suggestions.lastChild){suggestions.removeChild(suggestions.lastChild);}
return false;}}());